import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation = ({ history, showReceipt }) => {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const handlePrintLastReceipt = () => {
    if (history && history.length > 0) {
      const lastOrder = history[history.length - 1];
      showReceipt(lastOrder.id);
    } else {
      alert('Historie plateb je prázdná. Žádná účtenka k tisku.');
    }
  };

  return (
    <nav className="main-nav">
      <ul>
        <li>
          <Link to="/" className={isActive('/') ? 'active' : ''}>
            Objednávky
          </Link>
        </li>
        <li>
          <Link to="/new-order" className={isActive('/new-order') ? 'active' : ''}>
            Nová objednávka
          </Link>
        </li>
        <li>
          <Link to="/payment" className={isActive('/payment') ? 'active' : ''}>
            Platba
          </Link>
        </li>
        <li>
          <Link to="/history" className={isActive('/history') ? 'active' : ''}>
            Historie
          </Link>
        </li>
        <li>
          <Link to="/menu" className={isActive('/menu') ? 'active' : ''}>
            Menu
          </Link>
        </li>
      </ul>

      <div className="nav-footer">
        <div className="help-contact">
          <h4>Potřebujete pomoc?</h4>
          <p>Zavolejte na: <a href="tel:+420123456789">+420 123 456 789</a></p>
          <p>Email: <a href="mailto:podpora@restaurant.cz">podpora@restaurant.cz</a></p>
        </div>
        <div className="nav-actions">
          <button className="nav-action-btn" onClick={handlePrintLastReceipt}>
            Vytisknout poslední účtenku
          </button>
          <button className="nav-action-btn" onClick={() => alert('Zálohování dat...')}>
            Zálohovat data
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
