import React, { useState, useMemo } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import MenuItem from './MenuItem';
import { useNavigate } from 'react-router-dom';
import '../styles/order.scss';
import useLocalStorage from '../hooks/useLocalStorage';

// Assuming tables data is available globally or passed as a prop
// const tables = [
//     { id: 1, name: 'Stůl 1' },
//     { id: 2, name: 'Stůl 2' },
//     { id: 3, name: 'Stůl 3' },
//     { id: 4, name: 'Stůl 4' },
//     { id: 5, name: 'Stůl 5' }
// ]; // Placeholder: Replace with actual data source

// Category Filter Component
const CategoryFilter = ({ selectedCategory, setSelectedCategory, customCategories }) => {
  const defaultCategories = [
    { id: 'all', label: 'Vše' },
    { id: 'main', label: 'Hlavní jídla' },
    { id: 'drink', label: 'Nápoje' },
    { id: 'dessert', label: 'Dezerty' }
  ];

  return (
    <div className="category-filter">
      {defaultCategories.map(({ id, label }) => (
        <button
          key={id}
          className={`category-btn ${selectedCategory === id ? 'active' : ''}`}
          onClick={() => setSelectedCategory(id)}
        >
          {label}
        </button>
      ))}
      {customCategories?.map(category => (
        <button
          key={category}
          className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
          onClick={() => setSelectedCategory(category)}
        >
          {category}
        </button>
      ))}
    </div>
  );
};

// Table Selection Component
const TableSelection = ({ tables, onTableSelect }) => (
  <div className="table-selection">
    <h3>Vyberte stůl</h3>
    <div className="table-list">
      {tables.map(table => (
        <div 
          key={table.id} 
          className="table-item"
          role="button"
          aria-pressed={false}
          tabIndex={0}
          onClick={() => onTableSelect(table)}
        >
          {table.name}
        </div>
      ))}
    </div>
  </div>
);

// Order Summary Component
const OrderSummary = ({ currentOrder, calculateTotal, onSubmitOrder }) => (
  <div className="order-summary">
    <div className="total-amount">
      Celkem: <span>{calculateTotal()} Kč</span>
    </div>
    <button 
      className="submit-btn"
      disabled={currentOrder.length === 0}
      onClick={onSubmitOrder}
    >
      Odeslat objednávku
    </button>
  </div>
);

class NewOrder extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentOrder: [],
      selectedTable: null,
      selectedCategory: 'all',
    };

    // Bind methods
    this.onDragEnd = this.onDragEnd.bind(this);
    this.removeItem = this.removeItem.bind(this);
    this.calculateTotal = this.calculateTotal.bind(this);
    this.handleTableSelect = this.handleTableSelect.bind(this);
    this.handleSubmitOrder = this.handleSubmitOrder.bind(this);
  }

  // Derived state logic (replacing useMemo)
  getGroupedMenuItems() {
    const { menuItems, customCategories } = this.props;
    const grouped = menuItems.reduce((acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = [];
      }
      acc[item.category].push(item);
      return acc;
    }, {});

    const categoryOrder = ['main', 'drink', 'dessert', ...customCategories];
    return categoryOrder
      .filter(category => grouped[category])
      .map(category => ({
        category,
        items: grouped[category]
      }));
  }

  getFilteredMenuItems() {
    const { menuItems } = this.props;
    const { selectedCategory } = this.state;
    if (selectedCategory === 'all') {
      return menuItems;
    }
    return menuItems.filter(item => item.category === selectedCategory);
  }

  onDragEnd(result) {
    if (!result.destination) return;

    if (result.source.droppableId === 'menu' && result.destination.droppableId === 'order') {
      const { menuItems } = this.props;
      const item = menuItems.find(item => item.id === parseInt(result.draggableId));
      this.setState(prevState => ({
        currentOrder: [...prevState.currentOrder, { ...item }]
      }));
    }
  }

  removeItem(index) {
    this.setState(prevState => {
      const newOrder = [...prevState.currentOrder];
      newOrder.splice(index, 1);
      return { currentOrder: newOrder };
    });
  }

  calculateTotal() {
    return this.state.currentOrder.reduce((sum, item) => sum + item.price, 0);
  }

  handleTableSelect(table) {
    this.setState({ selectedTable: table });
  }

  handleSubmitOrder() {
    const { selectedTable, currentOrder } = this.state;
    const { setOrders, navigate } = this.props; // Get navigate from props

    if (!selectedTable) {
      alert('Vyberte prosím stůl.');
      return;
    }

    if (currentOrder.length === 0) {
      alert('Objednávka je prázdná!');
      return;
    }

    const order = {
      id: Date.now(),
      tableId: selectedTable.id,
      tableName: selectedTable.name,
      items: currentOrder,
      total: this.calculateTotal(),
      status: 'pending',
      timestamp: new Date().toISOString(),
    };

    setOrders(prevOrders => [...prevOrders, order]);

    this.setState({ currentOrder: [], selectedTable: null });

    alert('Objednávka byla úspěšně vytvořena!');
    navigate('/'); // Use navigate from props
  }

  renderMenuItems() {
    const { menuItems } = this.props;
    const { selectedCategory } = this.state;
    const groupedMenuItems = this.getGroupedMenuItems();
    const filteredMenuItems = this.getFilteredMenuItems();

    return (
      <Droppable droppableId="menu" isDropDisabled={true}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="menu-items"
          >
            {selectedCategory === 'all' ? (
              groupedMenuItems.map(({ category, items }) => (
                <div key={category} className="menu-category">
                  <h4 className="category-title">
                    {category === 'main' ? 'Hlavní jídla' :
                     category === 'drink' ? 'Nápoje' :
                     category === 'dessert' ? 'Dezerty' : category}
                  </h4>
                  {items.map((item, index) => (
                    <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <MenuItem item={item} />
                        </div>
                      )}
                    </Draggable>
                  ))}
                </div>
              ))
            ) : (
              filteredMenuItems.map((item, index) => (
                <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                    >
                      <MenuItem item={item} />
                    </div>
                  )}
                </Draggable>
              ))
            )}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    );
  }

  renderOrderItems() {
    const { currentOrder } = this.state;
    return (
      <Droppable droppableId="order">
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="order-items order-drop-zone"
          >
            {currentOrder.length === 0 ? (
              <div role="status" className="empty-order">Přetáhněte položky sem</div>
            ) : (
              currentOrder.map((item, index) => (
                <div key={index} className="order-item">
                  <span>{item.name} </span>
                  <span>{item.price} Kč</span>
                  <button
                    className="remove-btn"
                    aria-label="Odstranit položku"
                    onClick={() => this.removeItem(index)}
                  >
                    X
                  </button>
                </div>
              ))
            )}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    );
  }

  render() {
    const { menuItems, tables, customCategories } = this.props;
    const { selectedCategory, currentOrder, selectedTable } = this.state;

    return (
      <DragDropContext onDragEnd={this.onDragEnd}>
        <div className="new-order-container">
          <div className="menu-section">
            <h2>Menu</h2>
             {/* Pass selectedCategory, setSelectedCategory, customCategories as props */}
            <CategoryFilter
              selectedCategory={selectedCategory}
              setSelectedCategory={(category) => this.setState({ selectedCategory: category })}
              customCategories={customCategories}
            />
            {this.renderMenuItems()}
          </div>

          <div className="order-section">
            <h2>Objednávka</h2>
            {/* Pass tables and onTableSelect as props */}
            {!selectedTable ? (
                <TableSelection
                  tables={tables}
                  onTableSelect={this.handleTableSelect}
                />
              ) : (
                <> {/* Use a fragment here */}
                 <h3>Vybraný stůl: {selectedTable.name}</h3>
                 {this.renderOrderItems()}
                  {/* Pass currentOrder, calculateTotal, onSubmitOrder as props */}
                 <OrderSummary
                   currentOrder={currentOrder}
                   calculateTotal={this.calculateTotal}
                   onSubmitOrder={this.handleSubmitOrder}
                 />
                 <button
                    className="change-table-btn"
                    onClick={() => this.setState({ selectedTable: null })} // Add button to change table
                 >
                    Změnit stůl
                 </button>
                </>
              )}
          </div>
        </div>
      </DragDropContext>
    );
  }
}

