import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import useLocalStorage from './hooks/useLocalStorage';
import Navigation from './components/Navigation';
import Orders from './components/Orders';
import NewOrder from './components/NewOrder';
import History from './components/History';
import MenuManagement from './components/MenuManagement';
import Payment from './components/Payment';
import './styles/main.scss';

function App() {
  // Initialize all state with useLocalStorage
  const [orders, setOrders] = useLocalStorage('orders', []);
  const [history, setHistory] = useLocalStorage('history', []);
  const [menuItems, setMenuItems] = useLocalStorage('menuItems', [
    { id: 1, name: 'Kuřecí řízek', price: 150, category: 'main' },
    { id: 2, name: 'Hovězí guláš', price: 130, category: 'main' },
    { id: 3, name: 'Pivo', price: 35, category: 'drink' },
    { id: 4, name: 'Kola', price: 30, category: 'drink' },
    { id: 5, name: 'Zmrzlina', price: 45, category: 'dessert' },
    { id: 6, name: 'Bun Cha', price: 200, category: 'main' },
    { id: 7, name: 'Hranolky', price: 50, category: 'side' },
    { id: 8, name: 'Utopenec', price: 100, category: 'special' },
  ]);
  const [nextItemId, setNextItemId] = useLocalStorage('nextItemId', 9);
  const [categories, setCategories] = useLocalStorage('categories', ['main', 'drink', 'dessert', 'side', 'special']);
  const [tables] = useLocalStorage('tables', [
    { id: 1, name: 'Stůl 1' },
    { id: 2, name: 'Stůl 2' },
    { id: 3, name: 'Stůl 3' },
    { id: 4, name: 'Stůl 4' }
  ]);

  const updateOrderStatus = (orderId, newStatus) => {
    setOrders(orders.map(order => 
      order.id === orderId ? { ...order, status: newStatus } : order
    ));
  };

  const showLocation = (location) => {
    alert(`Vaše poloha: ${location.latitude}, ${location.longitude}`);
  };

  const showReceipt = (content) => {
    alert(content);
  };

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <h1>Restaurant Cash Register</h1>
          <div id="connectionStatus" className="connection-status">Online</div>
        </header>

        <div className="app-content">
          <Navigation />
          
          <main className="main-content">
            <Routes>
              <Route path="/" element={<Orders orders={orders} updateOrderStatus={updateOrderStatus} />} />
              <Route path="/new-order" element={<NewOrder menuItems={menuItems} tables={tables} setOrders={setOrders} />} />
              <Route path="/history" element={<History history={history} showLocation={showLocation} />} />
              <Route path="/menu" element={<MenuManagement menuItems={menuItems} setMenuItems={setMenuItems} nextItemId={nextItemId} setNextItemId={setNextItemId} categories={categories} setCategories={setCategories} />} />
              <Route path="/payment" element={<Payment orders={orders} setOrders={setOrders} setHistory={setHistory} showReceipt={showReceipt} />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
