import React, { useState, useMemo } from 'react';
// import useLocalStorage from '../hooks/useLocalStorage'; // Remove useLocalStorage

// Receive menuItems, setMenuItems, nextItemId, setNextItemId, categories, setCategories as props
const MenuManagement = ({ menuItems, setMenuItems, nextItemId, setNextItemId, categories, setCategories }) => {
  // menuItems, setMenuItems, nextItemId are now received as props
  // const [menuItems, setMenuItems] = useLocalStorage('menuItems', [
  //   { id: 1, name: 'Kuřecí řízek', price: 150, category: 'main' },
  //   { id: 2, name: 'Hovězí guláš', price: 130, category: 'main' },
  //   { id: 3, name: 'Pivo', price: 35, category: 'drink' },
  //   { id: 4, name: 'Kola', price: 30, category: 'drink' },
  //   { id: 5, name: 'Zmrzlina', price: 45, category: 'dessert' }
  // ]);
  
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    category: 'main'
  });
  const [editingId, setEditingId] = useState(null);
  // nextId is now received as a prop
  // const [nextId, setNextId] = useState(6);
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [isModalOpen, setIsModalOpen] = useState(false); // State for modal visibility
  const [newCategoryName, setNewCategoryName] = useState(''); // State for new category input

  // Group menu items by category and sort by the order in categories state
  const groupedMenuItems = useMemo(() => {
    const grouped = menuItems.reduce((acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = [];
      }
      acc[item.category].push(item);
      return acc;
    }, {});

    // Sort categories based on the order in the categories state
    return categories
      .filter(category => grouped[category]) // Only include categories with items
      .map(category => ({
        category,
        items: grouped[category]
      }));
  }, [menuItems, categories]); // Depend on menuItems and categories

  // Filter menu items based on selected category
  const filteredMenuItems = useMemo(() => {
    if (selectedCategory === 'all') {
      return menuItems;
    }
    return menuItems.filter(item => item.category === selectedCategory);
  }, [menuItems, selectedCategory]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'price' ? parseFloat(value) || '' : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.name || !formData.price) {
      alert('Vyplňte všechny údaje!');
      return;
    }
    
    if (editingId) {
      // Update existing item
      // Use setMenuItems prop
      setMenuItems(menuItems.map(item => 
        item.id === editingId ? { ...formData, id: editingId } : item
      ));
      setEditingId(null);
    } else {
      // Add new item
      // Use setMenuItems and setNextItemId props
      const newItem = { ...formData, id: nextItemId };
      console.log('Adding new item, calling setMenuItems with:', [...menuItems, newItem]);
      setMenuItems([...menuItems, newItem]);
      setNextItemId(nextItemId + 1);
    }
    
    // Reset form
    setFormData({ name: '', price: '', category: 'main' });
  };

  const editItem = (id) => {
    const itemToEdit = menuItems.find(item => item.id === id);
    if (itemToEdit) {
      setFormData({
        name: itemToEdit.name,
        price: itemToEdit.price,
        category: itemToEdit.category
      });
      setEditingId(id);
    }
  };

  const requestDeleteItem = (id) => {
    setDeleteConfirmId(id);
  };

  const confirmDelete = () => {
    // Use setMenuItems prop
    setMenuItems(menuItems.filter(item => item.id !== deleteConfirmId));
    setDeleteConfirmId(null);
  };

  const cancelDelete = () => {
    setDeleteConfirmId(null);
  };

  const handleAddNewCategory = (e) => {
    e.preventDefault();
    if (newCategoryName.trim() === '') {
      alert('Zadejte název kategorie');
      return;
    }
    setCategories([...categories, newCategoryName]);
    setNewCategoryName('');
    setIsModalOpen(false);
  };

  // Simple mapping for display names (can be expanded)
  const getCategoryDisplayName = (category) => {
    switch (category) {
      case 'main': return 'Hlavní jídla';
      case 'drink': return 'Nápoje';
      case 'dessert': return 'Dezerty';
      case 'side': return 'Přílohy';
      case 'special': return 'Speciály';
      default: return category.charAt(0).toUpperCase() + category.slice(1); // Capitalize others
    }
  };

  return (
    <div className="menu-management">
      <h2>Správa menu</h2>

      {/* Add/Edit Form */}
      <div className="menu-form card">
        <h3>{editingId ? 'Upravit položku' : 'Přidat novou položku'}</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="itemName">Název:</label>
            <input
              type="text"
              id="itemName"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="itemPrice">Cena:</label>
            <input
              type="number"
              id="itemPrice"
              name="price"
              value={formData.price}
              onChange={handleInputChange}
              required
              step="0.01"
            />
          </div>

          <div className="form-group">
            <label htmlFor="itemCategory">Kategorie:</label>
            <select
              id="itemCategory"
              name="category"
              value={formData.category}
              onChange={handleInputChange}
              required
            >
              {/* Populate with categories from state */}
              {categories.map(category => (
                <option key={category} value={category}>
                  {getCategoryDisplayName(category)}
                </option>
              ))}
            </select>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn btn-primary">
              {editingId ? 'Uložit změny' : 'Přidat položku'}
            </button>
            {editingId && (
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => { setEditingId(null); setFormData({ name: '', price: '', category: 'main' }); }}
              >
                Zrušit
              </button>
            )}
          </div>
        </form>
      </div>

      {/* Menu Items List */}
      <div className="menu-items-list">
        <h3>Existující položky</h3>
        
        {/* Category Filter */}
        <div className="category-filter">
          <button
            className={`category-btn ${selectedCategory === 'all' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('all')}
          >
            Vše
          </button>
          {/* Generate filter buttons from categories state */}
          {categories.map(category => (
             <button
               key={category}
               className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
               onClick={() => setSelectedCategory(category)}
             >
               {getCategoryDisplayName(category)}
             </button>
          ))}
          {/* Button to add new category */}
          <button
            className="category-btn add-category-btn"
            onClick={() => setIsModalOpen(true)}
          >
            + Přidat kategorii
          </button>
        </div>

        <div className="items-grid">
          {selectedCategory === 'all' ? (
            // Show grouped items when "all" is selected
            groupedMenuItems.map(({ category, items }) => (
              <div key={category} className="menu-category">
                <h4 className="category-title">
                  {category === 'main' ? 'Hlavní jídla' : 
                   category === 'drink' ? 'Nápoje' : 'Dezerty'}
                </h4>
                <div className="category-items">
                  {items.map(item => (
                    <div key={item.id} className="menu-item-card card">
                      <div className="item-info">
                        <h4>{item.name}</h4>
                        <p>{item.price} Kč</p>
                        <span className={`category-badge ${item.category}`}>
                          {getCategoryDisplayName(item.category)}
                        </span>
                      </div>
                      <div className="item-actions">
                        <button 
                          className="btn btn-outline btn-sm"
                          onClick={() => editItem(item.id)}
                        >
                          Upravit
                        </button>
                        <button 
                          className="btn btn-error btn-sm"
                          onClick={() => requestDeleteItem(item.id)}
                        >
                          Smazat
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))
          ) : (
            // Show filtered items when a specific category is selected
            filteredMenuItems.map(item => (
              <div key={item.id} className="menu-item-card card">
                <div className="item-info">
                  <h4>{item.name}</h4>
                  <p>{item.price} Kč</p>
                  <span className={`category-badge ${item.category}`}>
                    {getCategoryDisplayName(item.category)}
                  </span>
                </div>
                <div className="item-actions">
                  <button 
                    className="btn btn-outline btn-sm"
                    onClick={() => editItem(item.id)}
                  >
                    Upravit
                  </button>
                  <button 
                    className="btn btn-error btn-sm"
                    onClick={() => requestDeleteItem(item.id)}
                  >
                    Smazat
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Confirmation Dialog */}
      {deleteConfirmId !== null && (
        <div className="confirmation-dialog">
          <p>Opravdu chcete smazat tuto položku?</p>
          <div className="confirmation-actions">
            <button className="btn btn-error confirm-btn" onClick={confirmDelete}>Smazat</button>
            <button className="btn btn-secondary cancel-btn" onClick={cancelDelete}>Zrušit</button>
          </div>
        </div>
      )}

      {/* Add Category Modal */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Přidat novou kategorii</h2>
              <button className="close-button" onClick={() => setIsModalOpen(false)}>&times;</button>
            </div>
            <form onSubmit={handleAddNewCategory}>
              <div className="form-group">
                <label htmlFor="newCategoryName">Název kategorie:</label>
                <input
                  type="text"
                  id="newCategoryName"
                  value={newCategoryName}
                  onChange={(e) => setNewCategoryName(e.target.value)}
                  required
                />
              </div>
              <div className="form-actions">
                <button type="submit" className="btn btn-primary">Přidat</button>
                <button type="button" className="btn btn-secondary" onClick={() => setIsModalOpen(false)}>Zrušit</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default MenuManagement;
