import React, { useState } from 'react';

const OrderCard = ({ order, updateStatus, compact }) => {
  const [showStatusChange, setShowStatusChange] = useState(false);
  const [previousStatus, setPreviousStatus] = useState(null);

  const getStatusInfo = (status) => {
    switch(status) {
      case 'pending':
        return { text: 'Čeká na přípravu', class: 'pending' };
      case 'ready':
        return { text: 'Připraveno\nk platbě', class: 'ready' };
      case 'paid':
        return { text: 'Zaplaceno', class: 'paid' };
      default:
        return { text: status, class: '' };
    }
  };

  const statusInfo = getStatusInfo(order.status);
  const total = order.items.reduce((sum, item) => sum + item.price, 0);

  const handleStatusChange = (newStatus) => {
    setPreviousStatus(order.status);
    setShowStatusChange(true);
    updateStatus(order.id, newStatus);
  };

  const handleRevertStatus = () => {
    if (previousStatus) {
      updateStatus(order.id, previousStatus);
      setShowStatusChange(false);
      setPreviousStatus(null);
    }
  };

  return (
    <div className={`order-card ${compact ? 'compact' : ''}`}>
      <div className="order-header">
        <h3>Objednávka #{order.id.toString().slice(-4)}</h3>
        <div className="order-status">
          {showStatusChange ? (
            <div className="status-change">
              <span className={`status-badge ${statusInfo.class}`}>
                {statusInfo.text}
              </span>
              <button 
                className="revert-btn"
                onClick={handleRevertStatus}
                title="Vrátit předchozí stav"
              >
                ↩
              </button>
            </div>
          ) : (
            <span className={`status-badge ${statusInfo.class}`}>
              {statusInfo.text}
            </span>
          )}
        </div>
      </div>
      
      <div className="order-items">
        <table>
          <thead>
            <tr>
              <th className="item-name">Položka</th>
              <th className="quantity">Množství</th>
              <th className="price">Cena</th>
            </tr>
          </thead>
          <tbody>
            {order.items.map((item, index) => (
              <tr key={index}>
                <td className="item-name">{item.name}</td>
                <td className="quantity">{item.quantity}x</td>
                <td className="price">{item.price} Kč</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      <div className="order-footer">
        <div className="order-total">
          Celkem: {total} Kč
        </div>
        
        {!compact && updateStatus && (
          <div className="order-actions">
            {order.status === 'pending' && (
              <button 
                className="ready-btn"
                onClick={() => handleStatusChange('ready')}
              >
                Označit jako připravené
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderCard;
