import React from 'react';

const LocationModal = ({ show, info, onClose }) => {
  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content location-modal">
        <button className="close-btn" onClick={onClose}>×</button>
        <h2>Vaše poloha</h2>
        
        {info ? (
          <div className="location-info">
            <p>Zeměpisná šířka: {info.latitude}</p>
            <p>Zeměpisná délka: {info.longitude}</p>
            <p>Přesnost: {info.accuracy} metrů</p>
            
            <div className="location-visualization">
              <svg width="200" height="200" viewBox="0 0 200 200">
                <circle cx="100" cy="100" r="80" fill="lightblue" stroke="blue" strokeWidth="2"/>
                <circle cx="100" cy="100" r="3" fill="red"/>
                <text x="100" y="190" textAnchor="middle">Vaše poloha</text>
              </svg>
            </div>
          </div>
        ) : (
          <p>Nelze získat informace o poloze</p>
        )}
        
        <button className="close-modal-btn" onClick={onClose}>
          Zavřít
        </button>
      </div>
    </div>
  );
};

export default LocationModal;
