import React, { useState, useEffect } from 'react';
import OrderCard from './OrderCard';

const Orders = ({ orders, updateOrderStatus }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading
    const timer = setTimeout(() => setIsLoading(false), 500);
    return () => clearTimeout(timer);
  }, []);

  if (isLoading) return (
    <div 
      className="loading" 
      role="status"
      aria-label="Načítání objednávek"
    >
      Načítání...
    </div>
  );

  return (
    <div className="orders-container" role="main">
      <h2>Aktivní objednávky</h2>
      
      {orders.length === 0 ? (
        <div 
          className="empty-state"
          role="status"
          aria-label="Žádné aktivní objednávky"
        >
          Žádné aktivní objednávky
        </div>
      ) : (
        <div 
          className="orders-grid"
          role="list"
          aria-label="Seznam aktivních objednávek"
        >
          {orders.map(order => (
            <OrderCard 
              key={order.id} 
              order={order} 
              updateStatus={updateOrderStatus}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Orders;
