import React from 'react';
import '../styles/components.scss';

// Receive menuItems, setMenuItems, nextItemId, setNextItemId as props
class MenuManagement extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        name: '',
        price: '',
        category: 'main'
      },
      editingId: null,
      deleteConfirmId: null,
      selectedCategory: 'all',
      newCategoryName: '',
      showCategoryForm: false,
      showCategoryModal: false,
      editingCategories: [],
      isLoading: false
    };

    this.formRef = React.createRef();

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.editItem = this.editItem.bind(this);
    this.requestDeleteItem = this.requestDeleteItem.bind(this);
    this.confirmDelete = this.confirmDelete.bind(this);
    this.cancelDelete = this.cancelDelete.bind(this);
    this.handleAddCategory = this.handleAddCategory.bind(this);
    this.openCategoryModal = this.openCategoryModal.bind(this);
    this.handleCategoryChange = this.handleCategoryChange.bind(this);
    this.handleDeleteCategory = this.handleDeleteCategory.bind(this);
    this.handleSaveCategories = this.handleSaveCategories.bind(this);
    this.getCategoryDisplayName = this.getCategoryDisplayName.bind(this);
  }

  componentDidMount() {
    // Initialize editingCategories state from prop when modal is opened, not here
    // The useEffect for initializing customCategories is handled in App now.
    this.setState({ isLoading: false });
  }

  getGroupedMenuItems() {
    const { menuItems, customCategories } = this.props;
    const grouped = menuItems.reduce((acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = [];
      }
      acc[item.category].push(item);
      return acc;
    }, {});

    const categoryOrder = ['main', 'drink', 'dessert', ...customCategories];
    return categoryOrder
      .filter(category => grouped[category])
      .map(category => ({
        category,
        items: grouped[category]
      }));
  }

  getFilteredMenuItems() {
    const { menuItems } = this.props;
    const { selectedCategory } = this.state;
    if (selectedCategory === 'all') {
      return menuItems;
    }
    return menuItems.filter(item => item.category === selectedCategory);
  }

  handleInputChange(e) {
    const { name, value } = e.target;
    this.setState(prevState => ({
      formData: {
        ...prevState.formData,
        [name]: name === 'price' ? parseFloat(value) || '' : value
      }
    }));
  }

  handleSubmit(e) {
    e.preventDefault();
    const { formData, editingId } = this.state;
    const { menuItems, setMenuItems, nextItemId, setNextItemId } = this.props;

    if (!formData.name || !formData.price) {
      alert('Vyplňte všechny údaje!');
      return;
    }

    if (editingId) {
      setMenuItems(menuItems.map(item =>
        item.id === editingId ? { ...formData, id: editingId } : item
      ));
      this.setState({ editingId: null });
    } else {
      const newItem = { ...formData, id: nextItemId };
      setMenuItems([...menuItems, newItem]);
      setNextItemId(nextItemId + 1);
    }

    this.setState({ formData: { name: '', price: '', category: 'main' } });
  }

  editItem(id) {
    const { menuItems } = this.props;
    const itemToEdit = menuItems.find(item => item.id === id);
    if (itemToEdit) {
      this.setState({
        formData: {
          name: itemToEdit.name,
          price: itemToEdit.price,
          category: itemToEdit.category
        },
        editingId: id,
      });
      this.formRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }

  requestDeleteItem(id) {
    this.setState({ deleteConfirmId: id });
  }

  confirmDelete() {
    const { menuItems, setMenuItems } = this.props;
    const { deleteConfirmId } = this.state;
    setMenuItems(menuItems.filter(item => item.id !== deleteConfirmId));
    this.setState({ deleteConfirmId: null });
  }

  cancelDelete() {
    this.setState({ deleteConfirmId: null });
  }

  handleAddCategory() {
    const { newCategoryName } = this.state;
    const { customCategories, setCustomCategories } = this.props;
    if (newCategoryName.trim()) {
      if (customCategories.includes(newCategoryName.trim())) {
        alert('Tato kategorie již existuje!');
        return;
      }
      const updatedCategories = [...customCategories, newCategoryName.trim()];
      setCustomCategories(updatedCategories);
      this.setState({ newCategoryName: '', showCategoryForm: false });
    }
  }

  openCategoryModal() {
    this.setState({ editingCategories: [...this.props.customCategories], showCategoryModal: true });
  }

  handleCategoryChange(index, newName) {
    this.setState(prevState => {
      const updatedCategories = [...prevState.editingCategories];
      updatedCategories[index] = newName;
      return { editingCategories: updatedCategories };
    });
  }

  handleDeleteCategory(index) {
    const { editingCategories } = this.state;
    const { menuItems } = this.props;
    const categoryToDelete = editingCategories[index];
    const categoryInUse = menuItems.some(item => item.category === categoryToDelete);
    if (categoryInUse) {
      alert('Tuto kategorii nelze smazat, protože je používána v menu!');
      return;
    }
    this.setState(prevState => ({
      editingCategories: prevState.editingCategories.filter((_, i) => i !== index)
    }));
  }

  handleSaveCategories() {
    const { editingCategories } = this.state;
    const { menuItems, setMenuItems, setCustomCategories } = this.props;

    const uniqueCategories = new Set(editingCategories);
    if (uniqueCategories.size !== editingCategories.length) {
      alert('Kategorie nemohou mít stejný název!');
      return;
    }

    const updatedMenuItems = menuItems.map(item => {
      const oldCategory = item.category;
      const newCategory = editingCategories.find(cat => cat === oldCategory);
      return newCategory ? { ...item, category: newCategory } : item;
    });
    setMenuItems(updatedMenuItems);

    setCustomCategories(editingCategories);
    this.setState({ showCategoryModal: false });
  }

  getCategoryDisplayName(category) {
    switch(category) {
      case 'main':
        return 'Hlavní jídlo';
      case 'drink':
        return 'Nápoj';
      case 'dessert':
        return 'Dezert';
      default:
        return category;
    }
  }

  render() {
    const { formData, editingId, deleteConfirmId, selectedCategory, newCategoryName, showCategoryForm, showCategoryModal, editingCategories, isLoading } = this.state;
    const { menuItems, customCategories } = this.props;

    if (isLoading) {
      return (
        <div className="loading" role="status" aria-label="Načítání">
          Načítání...
        </div>
      );
    }

    const groupedMenuItems = this.getGroupedMenuItems();
    const filteredMenuItems = this.getFilteredMenuItems();

    return (
      <div className="menu-management" role="main">
        <h2>Správa menu</h2>

        <div className="menu-management-container">
          <div className="menu-form-section">
            <div className="menu-form card" ref={this.formRef} role="form" aria-label={editingId ? 'Upravit položku menu' : 'Přidat novou položku do menu'}>
              <h3>{editingId ? 'Upravit položku' : 'Přidat novou položku'}</h3>
              <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <label htmlFor="itemName">Název:</label>
                  <input
                    type="text"
                    id="itemName"
                    name="name"
                    value={formData.name}
                    onChange={this.handleInputChange}
                    required
                    aria-label="Název položky menu"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="itemPrice">Cena:</label>
                  <input
                    type="number"
                    id="itemPrice"
                    name="price"
                    value={formData.price}
                    onChange={this.handleInputChange}
                    required
                    step="0.01"
                    aria-label="Cena položky v Kč"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="itemCategory">Kategorie:</label>
                  <select
                    id="itemCategory"
                    name="category"
                    value={formData.category}
                    onChange={this.handleInputChange}
                    required
                    aria-label="Kategorie položky menu"
                  >
                    <option value="main">Hlavní jídlo</option>
                    <option value="drink">Nápoj</option>
                    <option value="dessert">Dezert</option>
                    {customCategories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>

                <div className="form-actions">
                  <button type="submit" className="submit-btn">
                    {editingId ? 'Uložit změny' : 'Přidat položku'}
                  </button>
                  {editingId && (
                    <button 
                      type="button" 
                      className="cancel-btn" 
                      onClick={() => this.setState({ editingId: null, formData: { name: '', price: '', category: 'main' } })}
                    >
                      Zrušit úpravu
                    </button>
                  )}
                </div>
              </form>
            </div>
          </div>

          <div className="menu-list-section">
            <div className="menu-list card">
              <h3>Seznam položek</h3>

              <div className="category-filter" role="tablist" aria-label="Filtry kategorií">
                <button
                  className={`category-btn ${selectedCategory === 'all' ? 'active' : ''}`}
                  onClick={() => this.setState({ selectedCategory: 'all' })}
                  role="tab"
                  aria-selected={selectedCategory === 'all'}
                  aria-label="Zobrazit všechny položky"
                >
                  Vše
                </button>
                {['main', 'drink', 'dessert'].map(category => (
                   <button
                     key={category}
                     className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
                     onClick={() => this.setState({ selectedCategory: category })}
                     role="tab"
                     aria-selected={selectedCategory === category}
                     aria-label={`Zobrazit ${this.getCategoryDisplayName(category)}`}
                   >
                     {this.getCategoryDisplayName(category)}
                   </button>
                ))}
                {customCategories.map(category => (
                   <button
                     key={category}
                     className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
                     onClick={() => this.setState({ selectedCategory: category })}
                     role="tab"
                     aria-selected={selectedCategory === category}
                     aria-label={`Zobrazit ${category}`}
                   >
                     {category}
                   </button>
                ))}
                <button
                   className="category-btn manage-categories-btn"
                   onClick={this.openCategoryModal}
                   aria-label="Spravovat kategorie"
                 >
                   Spravovat kategorie
                 </button>
              </div>

              <ul className="menu-items" role="tabpanel" aria-label="Seznam položek menu">
                {filteredMenuItems.length === 0 ? (
                  <li className="empty-state">Žádné položky k zobrazení</li>
                ) : (
                  filteredMenuItems.map(item => (
                    <li key={item.id} className="menu-item">
                      <div className="menu-item-content">
                        <span className="item-name">{item.name}</span>
                        <span className="item-price" aria-label={`${item.price} Kč`}>{item.price} Kč</span>
                      </div>
                      <div className="item-actions">
                        <button 
                          className="edit-btn" 
                          onClick={() => this.editItem(item.id)}
                          aria-label={`Upravit položku ${item.name}`}
                        >
                          Upravit
                        </button>
                        <button 
                          className="delete-btn" 
                          onClick={() => this.requestDeleteItem(item.id)}
                          aria-label={`Smazat položku ${item.name}`}
                        >
                          Smazat
                        </button>
                      </div>
                    </li>
                  ))
                )}
              </ul>
            </div>
          </div>
        </div>

        {deleteConfirmId && (
          <div className="modal-overlay" role="dialog" aria-modal="true" aria-labelledby="delete-confirm-title">
            <div className="modal-content">
              <h3 id="delete-confirm-title">Potvrdit smazání</h3>
              <p>Opravdu chcete smazat tuto položku?</p>
              <div className="modal-actions">
                <button 
                  className="delete-btn" 
                  onClick={this.confirmDelete}
                  aria-label="Potvrdit smazání"
                >
                  Smazat
                </button>
                <button 
                  className="cancel-btn" 
                  onClick={this.cancelDelete}
                  aria-label="Zrušit smazání"
                >
                  Zrušit
                </button>
              </div>
            </div>
          </div>
        )}

        {showCategoryModal && (
           <div className="modal-overlay" role="dialog" aria-modal="true" aria-labelledby="category-modal-title">
             <div className="modal-content">
               <h3 id="category-modal-title">Spravovat kategorie</h3>
               <div className="categories-list" role="list">
                 {editingCategories.map((category, index) => (
                   <div key={index} className="category-edit-item" role="listitem">
                     <input
                       type="text"
                       value={category}
                       onChange={(e) => this.handleCategoryChange(index, e.target.value)}
                       aria-label={`Upravit kategorii ${category}`}
                     />
                     <button 
                       onClick={() => this.handleDeleteCategory(index)} 
                       className="delete-btn"
                       aria-label={`Smazat kategorii ${category}`}
                     >
                       Smazat
                     </button>
                   </div>
                 ))}
                 <div className="category-edit-item" role="listitem">
                    <input
                       type="text"
                       placeholder="Nová kategorie"
                       value={newCategoryName}
                       onChange={(e) => this.setState({ newCategoryName: e.target.value })}
                       aria-label="Název nové kategorie"
                    />
                    <button 
                      onClick={this.handleAddCategory} 
                      className="add-btn"
                      aria-label="Přidat novou kategorii"
                    >
                      Přidat
                    </button>
                 </div>
               </div>
               <div className="modal-footer">
                 <button 
                   onClick={() => this.setState({ showCategoryModal: false, newCategoryName: '' })} 
                   className="cancel-btn"
                   aria-label="Zrušit úpravy kategorií"
                 >
                   Zrušit
                 </button>
                 <button 
                   onClick={this.handleSaveCategories} 
                   className="submit-btn"
                   aria-label="Uložit změny kategorií"
                 >
                   Uložit změny
                 </button>
               </div>
             </div>
           </div>
        )}
      </div>
    );
  }
}

export default MenuManagement;
