import React from 'react';

const OrderCard = ({ order, updateStatus, compact }) => {
  const getStatusInfo = (status) => {
    switch(status) {
      case 'pending':
        return { text: 'Čeká na přípravu', class: 'pending' };
      case 'ready':
        return { text: 'Připraveno k platbě', class: 'ready' };
      case 'paid':
        return { text: 'Zaplaceno', class: 'paid' };
      default:
        return { text: status, class: '' };
    }
  };

  const statusInfo = getStatusInfo(order.status);
  const total = order.items.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className={`order-card ${compact ? 'compact' : ''}`}>
      <div className="order-header">
        <h3>Objednávka #{order.id.toString().slice(-4)}</h3>
        <span className={`status-badge ${statusInfo.class}`}>
          {statusInfo.text}
        </span>
      </div>
      
      <div className="order-items">
        {order.items.map((item, index) => (
          <div key={index} className="order-item">
            <span>{item.name}</span>
            <span>{item.price} Kč</span>
          </div>
        ))}
      </div>
      
      <div className="order-footer">
        <div className="order-total">
          Celkem: {total} Kč
        </div>
        
        {!compact && updateStatus && (
          <div className="order-actions">
            {order.status === 'pending' && (
              <button 
                className="ready-btn"
                onClick={() => updateStatus(order.id, 'ready')}
              >
                Označit jako připravené
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderCard;
