import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import MenuItem from './MenuItem';
import '../styles/order.scss';

// Assuming tables data is available globally or passed as a prop
const tables = [
    { id: 1, name: 'Stůl 1' },
    { id: 2, name: 'Stůl 2' },
    { id: 3, name: 'Stůl 3' },
    { id: 4, name: 'Stůl 4' },
    { id: 5, name: 'Stůl 5' }
]; // Placeholder: Replace with actual data source

const NewOrder = () => {
  const [menuItems] = useState([
    { id: 1, name: 'Kuřecí řízek', price: 150, category: 'main' },
    { id: 2, name: 'Hovězí guláš', price: 130, category: 'main' },
    { id: 3, name: 'Pivo', price: 35, category: 'drink' },
    { id: 4, name: 'Kola', price: 30, category: 'drink' },
    { id: 5, name: 'Zmrzlina', price: 45, category: 'dessert' }
  ]);

  const [currentOrder, setCurrentOrder] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null); // New state for selected table

  const onDragEnd = (result) => {
    if (!result.destination) return;

    if (result.source.droppableId === 'menu' && result.destination.droppableId === 'order') {
      // Add item to order
      const item = menuItems.find(item => item.id === parseInt(result.draggableId));
      setCurrentOrder([...currentOrder, {...item}]);
    }
  };

  const removeItem = (index) => {
    const newOrder = [...currentOrder];
    newOrder.splice(index, 1);
    setCurrentOrder(newOrder);
  };

  const calculateTotal = () => {
    return currentOrder.reduce((sum, item) => sum + item.price, 0);
  };

  const handleTableSelect = (table) => {
    setSelectedTable(table);
  };

  const handleSubmitOrder = () => {
    if (!selectedTable) {
      alert('Please select a table first.'); // Or handle visually
      return;
    }

    // Create order object with tableId
    const order = {
      id: Date.now(), // Simple unique ID
      tableId: selectedTable.id,
      tableName: selectedTable.name,
      items: currentOrder,
      total: calculateTotal(),
      status: 'pending', // Initial status
      timestamp: new Date().toISOString(),
    };

    // Here you would save the order
    console.log('Submitting Order:', order);
    // Example: Call a function from your data management (script.js) to save the order
    // window.cashRegister.addOrder(order);

    // Reset state after submission
    setCurrentOrder([]);
    setSelectedTable(null); // Reset selected table after order is placed
    // Maybe navigate back to orders section or show a success message
  };

  return (
    <div className="new-order-container">
      <h2>Nová objednávka</h2>

      {!selectedTable ? (
        <div className="table-selection">
          <h3>Vyberte stůl</h3>
          <div className="table-list">
            {tables.map(table => (
              <div 
                key={table.id} 
                className="table-item"
                onClick={() => handleTableSelect(table)}
              >
                {table.name}
              </div>
            ))}
          </div>
        </div>
      ) : (
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="order-layout">
            <div className="menu-section">
              <h3>Menu</h3>
              <Droppable droppableId="menu" isDropDisabled={true}>
                {(provided) => (
                  <div 
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="menu-items"
                  >
                    {menuItems.map((item, index) => (
                      <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <MenuItem item={item} />
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </div>

            <div className="order-section">
              <h3>Objednávka pro {selectedTable.name}</h3>
              <Droppable droppableId="order">
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="order-items"
                  >
                    {currentOrder.length === 0 ? (
                      <div className="empty-order">Přetáhněte položky sem</div>
                    ) : (
                      currentOrder.map((item, index) => (
                        <div key={index} className="order-item">
                          <span>{item.name}</span>
                          <span>{item.price} Kč</span>
                          <button 
                            className="remove-btn"
                            onClick={() => removeItem(index)}
                          >
                            ×
                          </button>
                        </div>
                      ))
                    )}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>

              <div className="order-summary">
                <div className="total-amount">
                  Celkem: <span>{calculateTotal()} Kč</span>
                </div>
                <button 
                  className="submit-btn"
                  disabled={currentOrder.length === 0}
                  onClick={handleSubmitOrder} // Call new submit handler
                >
                  Odeslat objednávku
                </button>
              </div>
            </div>
          </div>
        </DragDropContext>
      )}
    </div>
  );
};

export default NewOrder;
