import React, { useState, useEffect } from 'react';
import { getLocation } from '../utils/geolocation';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const History = ({ history, showLocation }) => {
  const [filteredHistory, setFilteredHistory] = useState([]);
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    filterHistory(history);
  }, [history, dateFrom, dateTo]);

  const filterHistory = (currentHistory) => {
    let result = [...currentHistory];
    
    if (dateFrom) {
      result = result.filter(transaction => 
        new Date(transaction.paymentTimestamp) >= new Date(dateFrom));
    }
    
    if (dateTo) {
      result = result.filter(transaction => 
        new Date(transaction.paymentTimestamp) <= new Date(`${dateTo}T23:59:59`));
    }
    
    setFilteredHistory(result);
  };

  const calculateStats = () => {
    const revenue = filteredHistory.reduce((sum, transaction) => sum + transaction.total, 0);
    const count = filteredHistory.length;
    const averageTransaction = count > 0 ? revenue / count : 0;
    const totalItemsSold = filteredHistory.reduce((sum, transaction) => sum + transaction.items.length, 0);

    // Prepare data for revenue by month chart
    const monthlyRevenue = filteredHistory.reduce((acc, transaction) => {
      const date = new Date(transaction.paymentTimestamp);
      const month = date.toLocaleString('default', { month: 'short' });
      const year = date.getFullYear();
      const monthYear = `${month} ${year}`;
      acc[monthYear] = (acc[monthYear] || 0) + transaction.total;
      return acc;
    }, {});

    const chartData = {
      labels: Object.keys(monthlyRevenue).sort((a, b) => new Date(a) - new Date(b)),
      datasets: [
        {
          label: 'Tržba (Kč)',
          data: Object.values(monthlyRevenue),
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    const chartOptions = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          title: {
            display: true,
            text: 'Měsíční tržba',
          },
        },
        scales: {
            y: {
                beginAtZero: true,
            }
        }
      };

    return { revenue, count, averageTransaction, totalItemsSold, chartData, chartOptions }; // Return chart data and options
  };

  const handleShowLocation = async () => {
    setIsLoading(true);
    try {
      const location = await getLocation();
      showLocation(location);
    } catch (error) {
      alert(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const { revenue, count, averageTransaction, totalItemsSold, chartData, chartOptions } = calculateStats(); // Destructure chart data and options

  return (
    <div className="history-container">
      <h2>Historie tržeb</h2>
      
      <div className="history-filters">
        <div className="filter-group">
          <label>Od:</label>
          <input
            type="date"
            value={dateFrom}
            onChange={(e) => setDateFrom(e.target.value)}
          />
        </div>
        
        <div className="filter-group">
          <label>Do:</label>
          <input
            type="date"
            value={dateTo}
            onChange={(e) => setDateTo(e.target.value)}
          />
        </div>
        
        <button
          className="filter-btn"
          onClick={() => filterHistory(history)}
        >
          Filtrovat
        </button>
      </div>
      
      <div className="history-stats">
        <div className="stat-item">
          <span className="stat-label">Celková tržba:</span>
          <span className="stat-value">{revenue.toFixed(2)} Kč</span>
        </div>
        
        <div className="stat-item">
          <span className="stat-label">Počet transakcí:</span>
          <span className="stat-value">{count}</span>
        </div>

        <div className="stat-item">
          <span className="stat-label">Průměrná hodnota transakce:</span>
          <span className="stat-value">{averageTransaction.toFixed(2)} Kč</span>
        </div>
        
        <div className="stat-item">
          <span className="stat-label">Celkem prodaných položek:</span>
          <span className="stat-value">{totalItemsSold}</span>
        </div>
      </div>
      
      {/* Add chart */}
      {filteredHistory.length > 0 && (
        <div className="history-chart">
          <Bar data={chartData} options={chartOptions} />
        </div>
      )}
      
      <div className="history-list">
        {filteredHistory.length === 0 ? (
          <div className="empty-state">Žádné transakce v zadaném období</div>
        ) : (
          filteredHistory.map(transaction => (
            <div key={transaction.id} className="history-item">
              <div className="transaction-header">
                <h3>Transakce #${transaction.id.toString().slice(-4)}</h3>
                <p>Datum: ${new Date(transaction.paymentTimestamp).toLocaleString()}</p>
                {transaction.tableName && <p>Stůl: {transaction.tableName}</p>}
              </div>
              
              <div className="transaction-details">
                <div className="transaction-items">
                  {transaction.items.map((item, index) => (
                    <div key={index} className="transaction-item">
                      <span>{item.name}</span>
                      <span>{item.price} Kč</span>
                    </div>
                  ))}
                </div>
                
                <div className="transaction-summary">
                  <div className="transaction-total">
                    Celkem: {transaction.total} Kč
                  </div>
                  <div className="transaction-method">
                    Platba: {transaction.paymentMethod === 'cash' ? 'Hotovost' : 'Karta'}
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
      
      <button
        className="location-btn"
        onClick={handleShowLocation}
        disabled={isLoading}
      >
        {isLoading ? 'Načítání...' : 'Zobrazit moji polohu'}
      </button>
    </div>
  );
};

export default History;
