import React, { useState } from 'react';
import Header from './components/Header';
import Navigation from './components/Navigation';
import Orders from './components/Orders';
import NewOrder from './components/NewOrder';
import Payment from './components/Payment';
import History from './components/History';
import MenuManagement from './components/MenuManagement';
import ReceiptModal from './components/ReceiptModal';
import LocationModal from './components/LocationModal';
import './styles/main.scss';

const App = () => {
  const [activeSection, setActiveSection] = useState('orders');
  const [showReceipt, setShowReceipt] = useState(false);
  const [showLocation, setShowLocation] = useState(false);
  const [receiptContent, setReceiptContent] = useState('');
  const [locationInfo, setLocationInfo] = useState(null);

  const sections = {
    orders: <Orders />,
    newOrder: <NewOrder />,
    payment: <Payment showReceipt={handleShowReceipt} />,
    history: <History showLocation={handleShowLocation} />,
    menu: <MenuManagement />
  };

  function handleShowReceipt(content) {
    setReceiptContent(content);
    setShowReceipt(true);
  }

  function handleShowLocation(info) {
    setLocationInfo(info);
    setShowLocation(true);
  }

  return (
    <div className="app-container">
      <Header />
      <Navigation activeSection={activeSection} setActiveSection={setActiveSection} />
      
      <main className="main-content">
        <aside className="sidebar" style={{ minWidth: 220 }}>
          {/* Sidebar content or navigation can go here */}
        </aside>
        <section className="content-area">
          {sections[activeSection]}
        </section>
      </main>

      <ReceiptModal 
        show={showReceipt} 
        content={receiptContent}
        onClose={() => setShowReceipt(false)} 
      />
      
      <LocationModal 
        show={showLocation} 
        info={locationInfo}
        onClose={() => setShowLocation(false)} 
      />
    </div>
  );
};

export default App;
