import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation = () => {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="main-nav">
      <ul>
        <li>
          <Link to="/" className={isActive('/') ? 'active' : ''}>
            Objednávky
          </Link>
        </li>
        <li>
          <Link to="/new-order" className={isActive('/new-order') ? 'active' : ''}>
            Nová objednávka
          </Link>
        </li>
        <li>
          <Link to="/payment" className={isActive('/payment') ? 'active' : ''}>
            Platba
          </Link>
        </li>
        <li>
          <Link to="/history" className={isActive('/history') ? 'active' : ''}>
            Historie
          </Link>
        </li>
        <li>
          <Link to="/menu" className={isActive('/menu') ? 'active' : ''}>
            Menu
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export default Navigation;
