import React, { /* useState */ } from 'react';
import { BrowserRouter as Router, Routes, Route, /* Link */ } from 'react-router-dom';
import useLocalStorage from './hooks/useLocalStorage';
import Navigation from './components/Navigation';
import Orders from './components/Orders';
import NewOrder from './components/NewOrder';
import History from './components/History';
import MenuManagement from './components/MenuManagement';
import Payment from './components/Payment';
import './styles/main.scss';

function App() {
  // Initialize all state with useLocalStorage
  const [orders, setOrders] = useLocalStorage('orders', []);
  const [history, setHistory] = useLocalStorage('history', []);
  const [menuItems, setMenuItems] = useLocalStorage('menuItems', [
    { id: 1, name: 'Kuřecí řízek', price: 150, category: 'main' },
    { id: 2, name: 'Hovězí guláš', price: 130, category: 'main' },
    { id: 3, name: 'Pivo', price: 35, category: 'drink' },
    { id: 4, name: 'Kola', price: 30, category: 'drink' },
    { id: 5, name: 'Zmrzlina', price: 45, category: 'dessert' }
  ]);
  const [nextItemId, setNextItemId] = useLocalStorage('nextItemId', 6);
  const [tables] = useLocalStorage('tables', [
    { id: 1, name: 'Stůl 1' },
    { id: 2, name: 'Stůl 2' },
    { id: 3, name: 'Stůl 3' },
    { id: 4, name: 'Stůl 4' }
  ]);

  const updateOrderStatus = (orderId, newStatus) => {
    setOrders(orders.map(order => 
      order.id === orderId ? { ...order, status: newStatus } : order
    ));
  };

  const showLocation = (location) => {
    alert(`Vaše poloha: ${location.latitude}, ${location.longitude}`);
  };

  const showReceipt = (orderId) => {
    const order = history.find(item => item.id === orderId);
    if (!order) {
      alert('Účtenka nebyla nalezena.');
      return;
    }

    // Create receipt content
    const generateReceipt = (paidOrder) => {
      let receipt = '=== RESTAURACE ===';
      receipt += `Transakce #${paidOrder.id.toString().slice(-4)}\n`;
      receipt += `Datum: ${new Date(paidOrder.paymentTimestamp).toLocaleString()}\n\n`;
      receipt += `Stůl: ${paidOrder.tableName}\n`;
      receipt += `Číslo objednávky: ${paidOrder.id}\n\n`;
      receipt += '------------------\n\n';
      receipt += 'Položky:\n\n';
      paidOrder.items.forEach(item => {
        receipt += `${item.name} ${item.price} Kč\n`;
      });
      receipt += '\n------------------\n\n';
      receipt += `Celkem: ${paidOrder.total} Kč\n`;
      receipt += `Platba: ${paidOrder.paymentMethod === 'cash' ? 'Hotovost' : 'Karta'}\n\n`;
      receipt += '==================\n\n';
      receipt += 'Děkujeme za návštěvu!\n';
      receipt += 'Vítejte znovu\n\n';
      receipt += '=== Harukoid s.r.o. ===\n';
      return receipt;
    };

    // Create a new window for printing
    const printWindow = window.open('', '_blank');
    if (!printWindow) {
      alert('Pop-up blokován. Povolte prosím vyskakovací okna pro tisk účtenky.');
      return;
    }

    // Create the receipt HTML content
    const receiptContent = generateReceipt(order);
    const htmlContent = `
      <!DOCTYPE html>
      <html>
        <head>
          <title>Účtenka #${order.id.toString().slice(-4)}</title>
          <style>
            @page {
              size: 5.5in 8.5in;
              margin: 0;
            }
            body {
              font-family: monospace;
              white-space: pre;
              padding: 40px;
              font-size: 14px;
              line-height: 1.6;
              width: 5.5in;
              min-height: 8.5in;
              margin: 0;
              text-align: center;
            }
            @media print {
              body {
                padding: 40px;
                margin: 0;
                width: 5.5in;
                min-height: 8.5in;
                text-align: center;
              }
            }
          </style>
        </head>
        <body>
          ${receiptContent}
        </body>
      </html>
    `;

    // Write the content to the new window
    printWindow.document.write(htmlContent);
    printWindow.document.close();

    // Wait for content to load then print
    printWindow.onload = function() {
      printWindow.print();
      // Close the window after printing (or after user cancels)
      printWindow.onafterprint = function() {
        printWindow.close();
      };
    };
  };

  return (
    <Router>
      <div className="app">
        <div className="app-content">
          <Navigation history={history} showReceipt={showReceipt} />

          <main className="main-content">
            <header className="app-header">
              <div className="header-content">
                <h1>Restaurant Cash Register</h1>
                <div id="connectionStatus" className="connection-status">Online</div>
              </div>
            </header>
            <Routes>
              <Route path="/" element={<Orders orders={orders} updateOrderStatus={updateOrderStatus} />} />
              <Route path="/new-order" element={<NewOrder menuItems={menuItems} tables={tables} setOrders={setOrders} />} />
              <Route path="/history" element={<History history={history} showLocation={showLocation} />} />
              <Route path="/menu" element={<MenuManagement menuItems={menuItems} setMenuItems={setMenuItems} nextItemId={nextItemId} setNextItemId={setNextItemId} />} />
              <Route path="/payment" element={<Payment orders={orders} setOrders={setOrders} setHistory={setHistory} showReceipt={showReceipt} />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
