import React, { useState, useMemo } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import MenuItem from './MenuItem';
import { useNavigate } from 'react-router-dom';
import '../styles/order.scss';
import useLocalStorage from '../hooks/useLocalStorage';

// Assuming tables data is available globally or passed as a prop
// const tables = [
//     { id: 1, name: 'Stůl 1' },
//     { id: 2, name: 'Stůl 2' },
//     { id: 3, name: 'Stůl 3' },
//     { id: 4, name: 'Stůl 4' },
//     { id: 5, name: 'Stůl 5' }
// ]; // Placeholder: Replace with actual data source

// Category Filter Component
const CategoryFilter = ({ selectedCategory, setSelectedCategory, customCategories }) => {
  const defaultCategories = [
    { id: 'all', label: 'Vše' },
    { id: 'main', label: 'Hlavní jídla' },
    { id: 'drink', label: 'Nápoje' },
    { id: 'dessert', label: 'Dezerty' }
  ];

  return (
    <div className="category-filter">
      {defaultCategories.map(({ id, label }) => (
        <button
          key={id}
          className={`category-btn ${selectedCategory === id ? 'active' : ''}`}
          onClick={() => setSelectedCategory(id)}
        >
          {label}
        </button>
      ))}
      {customCategories?.map(category => (
        <button
          key={category}
          className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
          onClick={() => setSelectedCategory(category)}
        >
          {category}
        </button>
      ))}
    </div>
  );
};

// Table Selection Component
const TableSelection = ({ tables, onTableSelect }) => (
  <div className="table-selection">
    <h3>Vyberte stůl</h3>
    <div className="table-list">
      {tables.map(table => (
        <div 
          key={table.id} 
          className="table-item"
          role="button"
          aria-pressed={false}
          tabIndex={0}
          onClick={() => onTableSelect(table)}
        >
          {table.name}
        </div>
      ))}
    </div>
  </div>
);

// Order Summary Component
const OrderSummary = ({ currentOrder, calculateTotal, onSubmitOrder }) => (
  <div className="order-summary">
    <div className="total-amount">
      Celkem: <span>{calculateTotal()} Kč</span>
    </div>
    <button 
      className="submit-btn"
      disabled={currentOrder.length === 0}
      onClick={onSubmitOrder}
    >
      Odeslat objednávku
    </button>
  </div>
);

const NewOrder = ({ menuItems, tables, setOrders, customCategories }) => {
  const navigate = useNavigate();
  const [currentOrder, setCurrentOrder] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');

  // Group menu items by category
  const groupedMenuItems = useMemo(() => {
    const grouped = menuItems.reduce((acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = [];
      }
      acc[item.category].push(item);
      return acc;
    }, {});

    // Sort categories in a specific order
    const categoryOrder = ['main', 'drink', 'dessert', ...customCategories];
    return categoryOrder
      .filter(category => grouped[category])
      .map(category => ({
        category,
        items: grouped[category]
      }));
  }, [menuItems, customCategories]);

  // Filter menu items based on selected category
  const filteredMenuItems = useMemo(() => {
    if (selectedCategory === 'all') {
      return menuItems;
    }
    return menuItems.filter(item => item.category === selectedCategory);
  }, [menuItems, selectedCategory]);

  const onDragEnd = (result) => {
    if (!result.destination) return;

    if (result.source.droppableId === 'menu' && result.destination.droppableId === 'order') {
      // Add item to order
      const item = menuItems.find(item => item.id === parseInt(result.draggableId));
      setCurrentOrder([...currentOrder, {...item}]);
    }
  };

  const removeItem = (index) => {
    const newOrder = [...currentOrder];
    newOrder.splice(index, 1);
    setCurrentOrder(newOrder);
  };

  const calculateTotal = () => {
    return currentOrder.reduce((sum, item) => sum + item.price, 0);
  };

  const handleTableSelect = (table) => {
    setSelectedTable(table);
  };

  const handleSubmitOrder = () => {
    if (!selectedTable) {
      alert('Vyberte prosím stůl.');
      return;
    }

    if (currentOrder.length === 0) {
      alert('Objednávka je prázdná!');
      return;
    }

    // Create order object
    const order = {
      id: Date.now(),
      tableId: selectedTable.id,
      tableName: selectedTable.name,
      items: currentOrder,
      total: calculateTotal(),
      status: 'pending',
      timestamp: new Date().toISOString(),
    };

    // Add order to orders list
    setOrders(prevOrders => [...prevOrders, order]);

    // Reset state
    setCurrentOrder([]);
    setSelectedTable(null);

    // Show success message and navigate back to orders
    alert('Objednávka byla úspěšně vytvořena!');
    navigate('/');
  };

  // Render Methods
  const renderMenuItems = () => (
    <Droppable droppableId="menu" isDropDisabled={true}>
      {(provided) => (
        <div 
          ref={provided.innerRef}
          {...provided.droppableProps}
          className="menu-items"
        >
          {selectedCategory === 'all' ? (
            groupedMenuItems.map(({ category, items }) => (
              <div key={category} className="menu-category">
                <h4 className="category-title">
                  {category === 'main' ? 'Hlavní jídla' : 
                   category === 'drink' ? 'Nápoje' : 
                   category === 'dessert' ? 'Dezerty' : category}
                </h4>
                {items.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <MenuItem item={item} />
                      </div>
                    )}
                  </Draggable>
                ))}
              </div>
            ))
          ) : (
            filteredMenuItems.map((item, index) => (
              <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                  >
                    <MenuItem item={item} />
                  </div>
                )}
              </Draggable>
            ))
          )}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );

  const renderOrderItems = () => (
    <Droppable droppableId="order">
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.droppableProps}
          className="order-items order-drop-zone"
        >
          {currentOrder.length === 0 ? (
            <div role="status" className="empty-order">Přetáhněte položky sem</div>
          ) : (
            currentOrder.map((item, index) => (
              <div key={index} className="order-item">
                <span>{item.name} </span>
                <span>{item.price} Kč</span>
                <button 
                  className="remove-btn"
                  aria-label="Odstranit položku"
                  onClick={() => removeItem(index)}
                >
                  ×
                </button>
              </div>
            ))
          )} 
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );

  return (
    <div className="new-order-container">
      <h2>Nová objednávka</h2>

      {!selectedTable ? (
        <TableSelection 
          tables={tables} 
          onTableSelect={handleTableSelect} 
        />
      ) : (
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="order-form">
            <div className="section-header">
              <button 
                className="back-btn"
                aria-label="Zpět na výběr stolu"
                onClick={() => setSelectedTable(null)}
              >
                ← Zpět na výběr stolu
              </button>
              <h3>Objednávka pro {selectedTable.name}</h3>
            </div>

            <div className="order-content">
              <div className="menu-section">
                <CategoryFilter 
                  selectedCategory={selectedCategory}
                  setSelectedCategory={setSelectedCategory}
                  customCategories={customCategories}
                />
                {renderMenuItems()}
              </div>

              <div className="order-section">
                {renderOrderItems()}
                <OrderSummary 
                  currentOrder={currentOrder}
                  calculateTotal={calculateTotal}
                  onSubmitOrder={handleSubmitOrder}
                />
              </div>
            </div>
          </div>
        </DragDropContext>
      )}
    </div>
  );
};

export default NewOrder;
