import React, { useState, useEffect } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';

const MenuManagement = () => {
  const [menuItems, setMenuItems] = useLocalStorage('menuItems', [
    { id: 1, name: 'Kuřecí řízek', price: 150, category: 'main' },
    { id: 2, name: 'Hovězí guláš', price: 130, category: 'main' },
    { id: 3, name: 'Pivo', price: 35, category: 'drink' },
    { id: 4, name: 'Kola', price: 30, category: 'drink' },
    { id: 5, name: 'Zmrzlina', price: 45, category: 'dessert' }
  ]);
  
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    category: 'main'
  });
  const [editingId, setEditingId] = useState(null);
  const [nextId, setNextId] = useState(6);
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'price' ? parseFloat(value) || '' : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.name || !formData.price) {
      alert('Vyplňte všechny údaje!');
      return;
    }
    
    if (editingId) {
      // Update existing item
      setMenuItems(menuItems.map(item => 
        item.id === editingId ? { ...formData, id: editingId } : item
      ));
      setEditingId(null);
    } else {
      // Add new item
      setMenuItems([...menuItems, { ...formData, id: nextId }]);
      setNextId(nextId + 1);
    }
    
    // Reset form
    setFormData({ name: '', price: '', category: 'main' });
  };

  const editItem = (id) => {
    const itemToEdit = menuItems.find(item => item.id === id);
    if (itemToEdit) {
      setFormData({
        name: itemToEdit.name,
        price: itemToEdit.price,
        category: itemToEdit.category
      });
      setEditingId(id);
    }
  };

  const deleteItem = (id) => {
    setDeleteConfirmId(id);
  };

  const confirmDelete = () => {
    if (deleteConfirmId) {
      setMenuItems(menuItems.filter(item => item.id !== deleteConfirmId));
      setDeleteConfirmId(null);
    }
  };

  const cancelDelete = () => {
    setDeleteConfirmId(null);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setFormData({ name: '', price: '', category: 'main' });
  };

  return (
    <div className="menu-management-container">
      <h2>Správa menu</h2>
      
      {deleteConfirmId && (
        <div className="confirmation-dialog">
          <p>Opravdu chcete smazat tuto položku?</p>
          <div className="confirmation-actions">
            <button onClick={confirmDelete} className="confirm-btn">Ano</button>
            <button onClick={cancelDelete} className="cancel-btn">Ne</button>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="menu-form">
        <div className="form-group">
          <label>Název položky</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label>Cena (Kč)</label>
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            min="0"
            step="1"
            required
          />
        </div>
        
        <div className="form-group">
          <label>Kategorie</label>
          <select
            name="category"
            value={formData.category}
            onChange={handleInputChange}
          >
            <option value="main">Hlavní jídlo</option>
            <option value="drink">Nápoj</option>
            <option value="dessert">Dezert</option>
          </select>
        </div>
        
        <div className="form-actions">
          <button type="submit" className="submit-btn">
            {editingId ? 'Uložit změny' : 'Přidat položku'}
          </button>
          
          {editingId && (
            <button 
              type="button" 
              className="cancel-btn"
              onClick={cancelEdit}
            >
              Zrušit
            </button>
          )}
        </div>
      </form>
      
      <div className="menu-items-list">
        <h3>Položky menu</h3>
        
        {menuItems.length === 0 ? (
          <div className="empty-state">Žádné položky v menu</div>
        ) : (
          <div className="items-grid">
            {menuItems.map(item => (
              <div key={item.id} className="menu-item-card">
                <div className="item-info">
                  <h4>{item.name}</h4>
                  <p>{item.price} Kč</p>
                  <span className={`category-badge ${item.category}`}>
                    {item.category === 'main' ? 'Hlavní jídlo' : 
                     item.category === 'drink' ? 'Nápoj' : 'Dezert'}
                  </span>
                </div>
                
                <div className="item-actions">
                  <button 
                    className="edit-btn"
                    onClick={() => editItem(item.id)}
                  >
                    Upravit
                  </button>
                  
                  <button 
                    className="delete-btn"
                    onClick={() => deleteItem(item.id)}
                  >
                    Smazat
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MenuManagement;
