import React, { useState, useEffect } from 'react';
import OrderCard from './OrderCard';

const Payment = ({ orders, setOrders, setHistory, showReceipt }) => {
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [amountReceived, setAmountReceived] = useState('');
  const [change, setChange] = useState(0);
  const [selectedTable, setSelectedTable] = useState(null);

  const readyOrders = orders.filter(order => order.status === 'ready');

  const toggleOrderSelection = (orderId) => {
    setSelectedOrders(prev => 
      prev.includes(orderId) 
        ? prev.filter(id => id !== orderId) 
        : [...prev, orderId]
    );
  };

  const calculateTotal = () => {
    return readyOrders
      .filter(order => selectedOrders.includes(order.id))
      .reduce((sum, order) => sum + order.total, 0);
  };

  useEffect(() => {
    if (paymentMethod === 'cash' && amountReceived) {
      const total = calculateTotal();
      setChange(Math.max(0, parseFloat(amountReceived) - total));
    }
  }, [amountReceived, paymentMethod, selectedOrders]);

  const processPayment = () => {
    const total = calculateTotal();
    
    if (paymentMethod === 'cash' && parseFloat(amountReceived) < total) {
      alert('Obdržená částka je menší než celková cena!');
      return;
    }

    const paidOrders = orders.filter(order => selectedOrders.includes(order.id));
    const remainingOrders = orders.filter(order => !selectedOrders.includes(order.id));
    
    const now = new Date();
    const newHistoryEntries = paidOrders.map(order => ({
      ...order,
      status: 'paid',
      paymentMethod,
      paymentTimestamp: now.toISOString()
    }));

    setOrders(remainingOrders);
    setHistory(prevHistory => [...prevHistory, ...newHistoryEntries]);
    
    setSelectedOrders([]);
    setAmountReceived('');
    setChange(0);

    const receiptContent = generateReceipt(paidOrders, paymentMethod, total);
    showReceipt(receiptContent);
  };

  const generateReceipt = (paidOrders, method, total) => {
    let receipt = '=== RESTAURACE ===\n';
    receipt += `Datum: ${new Date().toLocaleString()}\n`;
    receipt += '------------------\n';
    
    paidOrders.forEach(order => {
      receipt += `Objednávka #${order.id.toString().slice(-4)}\n`;
      order.items.forEach(item => {
        receipt += `${item.name.padEnd(20, ' ')} ${item.price.toFixed(2).padStart(8, ' ')} Kč\n`;
      });
      receipt += `${'Celkem:'.padEnd(20, ' ')} ${order.total.toFixed(2).padStart(8, ' ')} Kč\n`;
      receipt += '------------------\n';
    });

    receipt += `${'Celková částka:'.padEnd(20, ' ')} ${total.toFixed(2).padStart(8, ' ')} Kč\n`;
    receipt += `Způsob platby: ${method === 'cash' ? 'Hotovost' : 'Karta'}\n`;

    if (method === 'cash') {
      receipt += `Obdrženo: ${parseFloat(amountReceived).toFixed(2)} Kč\n`;
      receipt += `Vráceno: ${change.toFixed(2)} Kč\n`;
    }

    receipt += '==================\n';
    receipt += 'Děkujeme za návštěvu!\n';

    return receipt;
  };

  return (
    <div className="payment-container">
      <h2>Platba</h2>
      
      {!selectedTable ? (
        <div className="table-selection">
          <h3>Vyberte stůl k platbě</h3>
          <div className="table-list">
            {[{ id: 1, name: 'Stůl 1' }, { id: 2, name: 'Stůl 2' }, { id: 3, name: 'Stůl 3' }, { id: 4, name: 'Stůl 4' }].map(table => (
              <div 
                key={table.id} 
                className="table-item"
                onClick={() => setSelectedTable(table)}
              >
                {table.name}
              </div>
            ))}
          </div>
        </div>
      ) : (
        <div className="payment-content">
          <div className="orders-section">
            <h3>Objednávky připravené k platbě pro {selectedTable.name}</h3>
            
            {readyOrders.filter(order => order.tableId === selectedTable.id).length === 0 ? (
              <div className="empty-state">Žádné objednávky připravené k platbě pro tento stůl</div>
            ) : (
              <div className="orders-list">
                {readyOrders.filter(order => order.tableId === selectedTable.id).map(order => (
                  <div key={order.id} className="payment-order-card">
                    <label>
                      <input
                        type="checkbox"
                        checked={selectedOrders.includes(order.id)}
                        onChange={() => toggleOrderSelection(order.id)}
                      />
                      <OrderCard order={order} compact />
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="payment-section">
            <h3>Platební údaje</h3>
            
            <div className="payment-methods">
              <label className="payment-method">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="cash"
                  checked={paymentMethod === 'cash'}
                  onChange={() => setPaymentMethod('cash')}
                />
                Hotovost
              </label>
              
              <label className="payment-method">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="card"
                  checked={paymentMethod === 'card'}
                  onChange={() => setPaymentMethod('card')}
                />
                Karta
              </label>
            </div>

            {paymentMethod === 'cash' && (
              <div className="cash-details">
                <div className="form-group">
                  <label>Obdržená částka (Kč)</label>
                  <input
                    type="number"
                    value={amountReceived}
                    onChange={(e) => setAmountReceived(e.target.value)}
                    min={calculateTotal()}
                    step="1"
                  />
                </div>
                
                <div className="change-amount">
                  Vráceno: <span>{change.toFixed(2)} Kč</span>
                </div>
              </div>
            )}

            <div className="payment-summary">
              <div className="total-amount">
                Celkem k platbě: <span>{calculateTotal().toFixed(2)} Kč</span>
              </div>
              
              <button
                className="process-payment-btn"
                onClick={processPayment}
                disabled={selectedOrders.length === 0 || (paymentMethod === 'cash' && !amountReceived)}
              >
                Zpracovat platbu
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Payment;
