import React, { useState, useMemo } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import MenuItem from './MenuItem';
import { useNavigate } from 'react-router-dom';
import '../styles/order.scss';

// Assuming tables data is available globally or passed as a prop
// const tables = [
//     { id: 1, name: 'Stůl 1' },
//     { id: 2, name: 'Stůl 2' },
//     { id: 3, name: 'Stůl 3' },
//     { id: 4, name: 'Stůl 4' },
//     { id: 5, name: 'Stůl 5' }
// ]; // Placeholder: Replace with actual data source

const NewOrder = ({ menuItems, tables, setOrders }) => {
  const navigate = useNavigate();
  const [currentOrder, setCurrentOrder] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');

  // Group menu items by category
  const groupedMenuItems = useMemo(() => {
    const grouped = menuItems.reduce((acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = [];
      }
      acc[item.category].push(item);
      return acc;
    }, {});

    // Sort categories in a specific order
    const categoryOrder = ['main', 'drink', 'dessert'];
    return categoryOrder
      .filter(category => grouped[category])
      .map(category => ({
        category,
        items: grouped[category]
      }));
  }, [menuItems]);

  // Filter menu items based on selected category
  const filteredMenuItems = useMemo(() => {
    if (selectedCategory === 'all') {
      return menuItems;
    }
    return menuItems.filter(item => item.category === selectedCategory);
  }, [menuItems, selectedCategory]);

  // We will now render ALL menu items as Draggables within the menu Droppable,
  // but control their visibility based on the selected category.
  // This provides a stable list of Draggables for react-beautiful-dnd.

  const onDragEnd = (result) => {
    console.log('Drag ended', result);
    if (!result.destination) {
      console.log('No destination');
      return;
    }

    console.log('Source:', result.source.droppableId, 'Destination:', result.destination.droppableId);

    // Find the item by ID from the current menuItems prop
    const draggedItem = menuItems.find(item => item.id === parseInt(result.draggableId));

    // If the dragged item is not found in the current menuItems, something is wrong. Abort.
    if (!draggedItem) {
        console.error('Dragged item not found in menuItems at the end of drag!', result.draggableId);
        // Optionally, you could try to revert the drag here if possible, or just log and return.
        return; 
    }

    if (result.source.droppableId === 'menu' && result.destination.droppableId === 'order') {
      console.log('Attempting to add item to order', result.draggableId);
      // Use the found draggedItem
      setCurrentOrder([...currentOrder, {...draggedItem}]);
      console.log('Item added to order', draggedItem);
    } // else if for other potential drop targets can go here later
  };

  const removeItem = (index) => {
    const newOrder = [...currentOrder];
    newOrder.splice(index, 1);
    setCurrentOrder(newOrder);
  };

  const calculateTotal = () => {
    return currentOrder.reduce((sum, item) => sum + item.price, 0);
  };

  const handleTableSelect = (table) => {
    setSelectedTable(table);
  };

  const handleSubmitOrder = () => {
    if (!selectedTable) {
      alert('Vyberte prosím stůl.');
      return;
    }

    if (currentOrder.length === 0) {
      alert('Objednávka je prázdná!');
      return;
    }

    // Create order object
    const order = {
      id: Date.now(),
      tableId: selectedTable.id,
      tableName: selectedTable.name,
      items: currentOrder,
      total: calculateTotal(),
      status: 'pending',
      timestamp: new Date().toISOString(),
    };

    // Add order to orders list
    setOrders(prevOrders => [...prevOrders, order]);

    // Reset state
    setCurrentOrder([]);
    setSelectedTable(null);

    // Show success message and navigate back to orders
    alert('Objednávka byla úspěšně vytvořena!');
    navigate('/');
  };

  return (
    <div className="new-order-container">
      <h2>Nová objednávka</h2>

      {!selectedTable ? (
        <div className="table-selection">
          <h3>Vyberte stůl</h3>
          <div className="table-list">
            {tables.map(table => (
              <div 
                key={table.id} 
                className="table-item"
                onClick={() => handleTableSelect(table)}
              >
                {table.name}
              </div>
            ))}
          </div>
        </div>
      ) : (
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="order-form">
          <div className="menu-section">
            <h3>Menu</h3>

            {/* Category Filter */}
            <div className="category-filter">
              <button
                className={`category-btn ${selectedCategory === 'all' ? 'active' : ''}`}
                onClick={() => setSelectedCategory('all')}
              >
                Vše
              </button>
              <button
                className={`category-btn ${selectedCategory === 'main' ? 'active' : ''}`}
                onClick={() => setSelectedCategory('main')}
              >
                Hlavní jídla
              </button>
              <button
                className={`category-btn ${selectedCategory === 'drink' ? 'active' : ''}`}
                onClick={() => setSelectedCategory('drink')}
              >
                Nápoje
              </button>
              <button
                className={`category-btn ${selectedCategory === 'dessert' ? 'active' : ''}`}
                onClick={() => setSelectedCategory('dessert')}
              >
                Dezerty
              </button>
            </div>

            <Droppable droppableId="menu" isDropDisabled={true}>
              {(provided) => (
                <div 
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="menu-items"
                >
                  {/* Always render all menu items as Draggables, but filter visibility */}
                  {menuItems.map((item, index) => {
                    const isVisible = selectedCategory === 'all' || item.category === selectedCategory;
                    // Use item.id as index is not stable when filtering/sorting
                    return ( // Moved return outside Draggable map
                      <Draggable key={item.id} draggableId={item.id.toString()} index={index}> {/* Keep original index or use a stable one */}
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            // Apply inline style or class to control visibility
                            style={{
                              ...provided.draggableProps.style,
                              display: isVisible ? 'block' : 'none', // Hide if not visible
                              // Add other necessary drag-related styles if needed
                              padding: '1rem 1.5rem', // Match existing item padding
                              marginBottom: '1rem', // Match existing item margin
                              background: 'var(--bg-primary)', // Match existing background
                              borderRadius: 'var(--radius-md)', // Match existing border radius
                              cursor: 'grab', // Match existing cursor
                              transition: 'transform 0.2s, box-shadow 0.2s', // Match existing transition
                              border: '1px solid var(--border-color)', // Match existing border
                            }}
                          >
                            <MenuItem item={item} />
                          </div>
                        )}
                      </Draggable>
                    );
                  })}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>

          <div className="order-section">
              <h3>Objednávka pro {selectedTable.name}</h3>
            <Droppable droppableId="order">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="order-items order-drop-zone"
                >
                  {currentOrder.length === 0 ? (
                    <div className="empty-order">Přetáhněte položky sem</div>
                  ) : (
                    currentOrder.map((item, index) => (
                      <div key={index} className="order-item">
                        <span>{item.name}</span>
                        <span>{item.price} Kč</span>
                        <button 
                          className="remove-btn"
                          onClick={() => removeItem(index)}
                        >
                          ×
                        </button>
                      </div>
                    ))
                  )}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>

            <div className="order-summary">
              <div className="total-amount">
                Celkem: <span>{calculateTotal()} Kč</span>
              </div>
              <button 
                className="submit-btn"
                disabled={currentOrder.length === 0}
                  onClick={handleSubmitOrder}
              >
                Odeslat objednávku
              </button>
            </div>
          </div>
        </div>
      </DragDropContext>
      )}
    </div>
  );
};

export default NewOrder;
