import React, { Component } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import MenuItem from './MenuItem';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/components.scss';
import PropTypes from 'prop-types';

// Category Filter Component
const CategoryFilter = ({ selectedCategory, setSelectedCategory, customCategories }) => {
  const defaultCategories = [
    { id: 'all', label: 'Vše' },
    { id: 'main', label: 'Hlavní jídla' },
    { id: 'drink', label: 'Nápoje' },
    { id: 'dessert', label: 'Dezerty' }
  ];

  return (
    <div className="category-filter" role="tablist" aria-label="Filtry kategorií">
      {defaultCategories.map(({ id, label }) => (
        <button
          key={id}
          className={`category-btn ${selectedCategory === id ? 'active' : ''}`}
          onClick={() => setSelectedCategory(id)}
          role="tab"
          aria-selected={selectedCategory === id}
          aria-label={`Zobrazit ${label}`}
        >
          {label}
        </button>
      ))}
      {customCategories?.map(category => (
        <button
          key={category}
          className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
          onClick={() => setSelectedCategory(category)}
          role="tab"
          aria-selected={selectedCategory === category}
          aria-label={`Zobrazit ${category}`}
        >
          {category}
        </button>
      ))}
    </div>
  );
};

// Table Selection Component
const TableSelection = ({ tables, onTableSelect }) => (
  <div className="table-selection">
    <h3>Vyberte stůl</h3>
    <div className="table-list" role="radiogroup" aria-label="Výběr stolu">
      {tables.map(table => (
        <div 
          key={table.id} 
          className="table-item"
          role="radio"
          aria-checked={false}
          tabIndex={0}
          onClick={() => onTableSelect(table)}
          aria-label={`Stůl ${table.name}`}
        >
          {table.name}
        </div>
      ))}
    </div>
  </div>
);

// Order Summary Component
const OrderSummary = ({ currentOrder, calculateTotal, onSubmitOrder }) => (
  <div className="order-summary">
    <div className="total-amount" role="status">
      Celkem: <span aria-label={`Celková částka ${calculateTotal()} Kč`}>{calculateTotal()} Kč</span>
    </div>
    <button 
      className="submit-btn"
      disabled={currentOrder.length === 0}
      onClick={onSubmitOrder}
      aria-label="Odeslat objednávku"
    >
      Odeslat objednávku
    </button>
  </div>
);

// Wrapper component to inject location and navigate for class components
function withLocationAndNavigation(Component) {
  return function WrappedComponent(props) {
    const location = useLocation();
    const navigate = useNavigate();
    return <Component {...props} location={location} navigate={navigate} />;
  };
}

class NewOrder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentOrder: [],
      selectedTable: null,
      selectedCategory: 'Vše',
      isLoading: true,
    };

    this.menuItemsRef = React.createRef(); // Ref for menu items container

    // Bind methods
    this.onDragEnd = this.onDragEnd.bind(this);
    this.removeItem = this.removeItem.bind(this);
    this.calculateTotal = this.calculateTotal.bind(this);
    this.handleTableSelect = this.handleTableSelect.bind(this);
    this.handleSubmitOrder = this.handleSubmitOrder.bind(this);
    this.handleCategoryChange = this.handleCategoryChange.bind(this);
  }

  componentDidMount() {
    // Simulate fetching data
    setTimeout(() => {
      this.setState({
        isLoading: false,
        // Assuming menuItems and tables are passed as props
      });
    }, 500);
  }

  getGroupedMenuItems() {
    const { menuItems } = this.props;
    const grouped = menuItems.reduce((acc, item) => {
      const category = item.category || 'Uncategorized'; // Handle items without category
      if (!acc[category]) {
        acc[category] = [];
      }
      acc[category].push(item);
      return acc;
    }, {});

    // Define a preferred order for categories, putting defaults first
    const preferredOrder = ['main', 'drink', 'dessert'];
    const allCategories = Object.keys(grouped);

    // Sort categories with preferred order first, then others alphabetically
    const sortedCategories = allCategories.sort((a, b) => {
      const aIndex = preferredOrder.indexOf(a);
      const bIndex = preferredOrder.indexOf(b);

      if (aIndex !== -1 && bIndex !== -1) {
        return aIndex - bIndex; // Both in preferred order, sort by index
      } else if (aIndex !== -1) {
        return -1; // A is in preferred order, A comes first
      } else if (bIndex !== -1) {
        return 1; // B is in preferred order, B comes first
      } else {
        return a.localeCompare(b); // Neither in preferred order, sort alphabetically
      }
    });

    // Return grouped items in the sorted order
    return sortedCategories.map(category => ({
      category,
      items: grouped[category]
    }));
  }

  getFilteredMenuItems() {
    const { menuItems } = this.props;
    const { selectedCategory } = this.state;
    if (selectedCategory === 'Vše') {
      return menuItems;
    }
    const grouped = this.getGroupedMenuItems().reduce((acc, group) => {
        acc[group.category] = group.items;
        return acc;
      }, {});

    return grouped[selectedCategory] || [];
  }

  handleCategoryChange(category) {
    this.setState({ selectedCategory: category });
  }

  onDragEnd(result) {
    if (!result.destination) return;

    if (result.source.droppableId === 'menu-drop-area' && result.destination.droppableId === 'order-drop-area') {
      const { menuItems } = this.props;
      const item = menuItems.find(item => item.id === parseInt(result.draggableId));
      if (item) {
        this.setState(prevState => ({
          currentOrder: [...prevState.currentOrder, { ...item }]
        }));
      }
    }
  }

  removeItem(index) {
    this.setState(prevState => {
      const newOrder = [...prevState.currentOrder];
      newOrder.splice(index, 1);
      return { currentOrder: newOrder };
    });
  }

  calculateTotal() {
    return this.state.currentOrder.reduce((sum, item) => sum + item.price, 0);
  }

  handleTableSelect(table) {
    this.setState({ selectedTable: table });
  }

  handleSubmitOrder() {
    const { selectedTable, currentOrder } = this.state;
    const { setOrders, navigate } = this.props;

    if (!selectedTable) {
      alert('Vyberte prosím stůl.');
      return;
    }

    if (currentOrder.length === 0) {
      alert('Objednávka je prázdná!');
      return;
    }

    const order = {
      id: Date.now(),
      tableId: selectedTable.id,
      tableName: selectedTable.name,
      items: currentOrder,
      total: this.calculateTotal(),
      status: 'pending',
      timestamp: new Date().toISOString(),
    };

    setOrders(prevOrders => [...prevOrders, order]);
    this.setState({ currentOrder: [], selectedTable: null });
    alert('Objednávka byla úspěšně vytvořena!');
    navigate('/');
  }

  renderMenuItems(item) {
    return (
      <Draggable key={item.id} draggableId={item.id.toString()} index={0}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
          >
            <MenuItem item={item} />
          </div>
        )}
      </Draggable>
    );
  }

  renderOrderItems(item, index) {
    return (
      <div key={index} className="order-item" role="listitem">
        <span>{item.name} </span>
        <span aria-label={`${item.price} Kč`}>{item.price} Kč</span>
        <button
          className="remove-btn"
          aria-label="Odstranit položku"
          onClick={() => this.removeItem(index)}
        >
          X
        </button>
      </div>
    );
  }

  render() {
    const { currentOrder, selectedTable, selectedCategory, isLoading } = this.state;
    const { menuItems, tables, customCategories } = this.props;

    const groupedMenuItems = this.getGroupedMenuItems();
    const filteredMenuItems = selectedCategory === 'Vše'
      ? menuItems
      : groupedMenuItems.find(group => group.category === selectedCategory)?.items || [];

    if (isLoading) {
      return <div className="loading">Načítání...</div>;
    }

    const defaultCategories = ['main', 'drink', 'dessert'];
    const allCategories = ['Vše', ...defaultCategories, ...customCategories.map(cat => cat.name)];

    return (
      <DragDropContext onDragEnd={this.onDragEnd}>
        <div className="new-order-container">
          <section className="menu-section" aria-label="Výběr položek menu">
            <div className="category-filter" role="tablist" aria-label="Filtry kategorií">
               {/* Render category buttons */}
               {allCategories.map(category => (
                  <button
                    key={category}
                    className={selectedCategory === category ? 'category-btn active' : 'category-btn'}
                    onClick={() => this.handleCategoryChange(category)}
                    role="tab"
                    aria-selected={selectedCategory === category}
                  >
                    {category === 'main' ? 'Hlavní jídlo' : category === 'drink' ? 'Nápoje' : category === 'dessert' ? 'Dezerty' : category}
                  </button>
                ))}
            </div>

            <Droppable droppableId="menu-drop-area" isDropDisabled={true}>
              {(provided) => (
                <div className="menu-items" ref={provided.innerRef} {...provided.droppableProps} role="tabpanel">
                  {selectedCategory === 'Vše' ? (
                    // Render all menu items grouped by category
                    groupedMenuItems.map((categoryGroup) => (
                      categoryGroup.items.length > 0 && (
                        <section key={categoryGroup.category} className="menu-category" aria-label={`${categoryGroup.category} menu items`}>
                          <h4 className="category-title">{categoryGroup.category === 'main' ? 'Hlavní jídlo' : categoryGroup.category === 'drink' ? 'Nápoje' : categoryGroup.category === 'dessert' ? 'Dezerty' : categoryGroup.category}</h4>
                          {categoryGroup.items.map((item, index) => (
                             <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                                {(provided) => (
                                  <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                  >
                                    <MenuItem item={item} />
                                  </div>
                                )}
                              </Draggable>
                           ))}
                        </section>
                      )
                    ))
                  ) : (
                    // Render filtered menu items
                    filteredMenuItems.length > 0 ? (
                      <section className="menu-category" aria-label={`${selectedCategory} menu items`}>
                         {/* Hide category title when a specific category is selected */}
                         {/* <h4 className="category-title">{selectedCategory}</h4> */}
                        {filteredMenuItems.map((item, index) => (
                           <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                              {(provided) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                >
                                  <MenuItem item={item} />
                                </div>
                              )}
                            </Draggable>
                         ))}
                      </section>
                    ) : (
                      <div className="empty-state">Žádné položky v této kategorii.</div>
                    )
                  )}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </section>

          <section className="order-section" aria-label="Aktuální objednávka">
             <div className="selected-table">
                {selectedTable ? (
                  <h3>Stůl: {selectedTable.number}</h3>
                ) : (
                  <h3>Vyberte stůl</h3>
                )}
                {/* Add button to open table selection modal if needed */}
             </div>

            <Droppable droppableId="order-drop-area">
              {(provided) => (
                <div className="order-items" {...provided.droppableProps} ref={provided.innerRef} aria-label="Seznam objednaných položek">
                  {currentOrder.length === 0 ? (
                    <div className="empty-order">Přetáhněte položky menu sem</div>
                  ) : (
                    currentOrder.map((item, index) => this.renderOrderItems(item, index))
                  )}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>

            <div className="order-summary">
              <h3 className="total-amount">Celkem: <span>{this.calculateTotal()} Kč</span></h3>
              <button
                className="submit-btn"
                onClick={this.handleSubmitOrder}
                disabled={currentOrder.length === 0 || !selectedTable}
              >
                Odeslat objednávku
              </button>
            </div>

             {/* Table Selection Modal placeholder */}
             {/* <Modal isOpen={showTableModal} onClose={this.handleCloseTableModal}> */}
             {/*   <TableSelection tables={tables} onSelect={this.handleTableSelect} onClose={this.handleCloseTableModal} /> */}
             {/* </Modal> */}

          </section>
        </div>
      </DragDropContext>
    );
  }
}

NewOrder.propTypes = {
  menuItems: PropTypes.array.isRequired,
  tables: PropTypes.array.isRequired,
  customCategories: PropTypes.array.isRequired,
  location: PropTypes.object.isRequired,
  navigate: PropTypes.func.isRequired,
  setOrders: PropTypes.func.isRequired,
};

const NewOrderWithLocationAndNavigation = withLocationAndNavigation(NewOrder);

export default NewOrderWithLocationAndNavigation;
