import React, { useState, useMemo, useRef, useEffect } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';

// Receive menuItems, setMenuItems, nextItemId, setNextItemId as props
const MenuManagement = ({ menuItems, setMenuItems, nextItemId, setNextItemId }) => {
  // menuItems, setMenuItems, nextItemId are now received as props
  // const [menuItems, setMenuItems] = useLocalStorage('menuItems', [
  //   { id: 1, name: 'Kuřecí řízek', price: 150, category: 'main' },
  //   { id: 2, name: 'Hovězí guláš', price: 130, category: 'main' },
  //   { id: 3, name: 'Pivo', price: 35, category: 'drink' },
  //   { id: 4, name: 'Kola', price: 30, category: 'drink' },
  //   { id: 5, name: 'Zmrzlina', price: 45, category: 'dessert' }
  // ]);

  const [formData, setFormData] = useState({
    name: '',
    price: '',
    category: 'main'
  });
  const [editingId, setEditingId] = useState(null);
  // nextId is now received as a prop
  // const [nextId, setNextId] = useState(6);
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [customCategories, setCustomCategories] = useLocalStorage('customCategories', []);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [showCategoryForm, setShowCategoryForm] = useState(false);
  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [editingCategories, setEditingCategories] = useState([]);
  const formRef = useRef(null);

  // Initialize custom categories if empty
  useEffect(() => {
    if (!customCategories || customCategories.length === 0) {
      setCustomCategories([]);
    }
  }, []);

  // Group menu items by category
  const groupedMenuItems = useMemo(() => {
    const grouped = menuItems.reduce((acc, item) => {
      if (!acc[item.category]) {
        acc[item.category] = [];
      }
      acc[item.category].push(item);
      return acc;
    }, {});

    // Sort categories in a specific order
    const categoryOrder = ['main', 'drink', 'dessert', ...customCategories];
    return categoryOrder
      .filter(category => grouped[category])
      .map(category => ({
        category,
        items: grouped[category]
      }));
  }, [menuItems, customCategories]);

  // Filter menu items based on selected category
  const filteredMenuItems = useMemo(() => {
    if (selectedCategory === 'all') {
      return menuItems;
    }
    return menuItems.filter(item => item.category === selectedCategory);
  }, [menuItems, selectedCategory]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'price' ? parseFloat(value) || '' : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price) {
      alert('Vyplňte všechny údaje!');
      return;
    }

    if (editingId) {
      // Update existing item
      // Use setMenuItems prop
      setMenuItems(menuItems.map(item => 
        item.id === editingId ? { ...formData, id: editingId } : item
      ));
      setEditingId(null);
    } else {
      // Add new item
      // Use setMenuItems and setNextItemId props
      const newItem = { ...formData, id: nextItemId };
      setMenuItems([...menuItems, newItem]);
      setNextItemId(nextItemId + 1);
    }

    // Reset form
    setFormData({ name: '', price: '', category: 'main' });
  };

  const editItem = (id) => {
    const itemToEdit = menuItems.find(item => item.id === id);
    if (itemToEdit) {
      setFormData({
        name: itemToEdit.name,
        price: itemToEdit.price,
        category: itemToEdit.category
      });
      setEditingId(id);
      // Scroll to form with smooth behavior
      formRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const requestDeleteItem = (id) => {
    setDeleteConfirmId(id);
  };

  const confirmDelete = () => {
    // Use setMenuItems prop
    setMenuItems(menuItems.filter(item => item.id !== deleteConfirmId));
    setDeleteConfirmId(null);
  };

  const cancelDelete = () => {
    setDeleteConfirmId(null);
  };

  const handleAddCategory = () => {
    if (newCategoryName.trim()) {
      // Check if category already exists
      if (customCategories.includes(newCategoryName.trim())) {
        alert('Tato kategorie již existuje!');
        return;
      }
      // Add new category
      const updatedCategories = [...customCategories, newCategoryName.trim()];
      setCustomCategories(updatedCategories);
      setNewCategoryName('');
      setShowCategoryForm(false);
    }
  };

  const openCategoryModal = () => {
    setEditingCategories([...customCategories]);
    setShowCategoryModal(true);
  };

  const handleCategoryChange = (index, newName) => {
    const updatedCategories = [...editingCategories];
    updatedCategories[index] = newName;
    setEditingCategories(updatedCategories);
  };

  const handleDeleteCategory = (index) => {
    const categoryToDelete = editingCategories[index];
    // Check if category is in use
    const categoryInUse = menuItems.some(item => item.category === categoryToDelete);
    if (categoryInUse) {
      alert('Tuto kategorii nelze smazat, protože je používána v menu!');
      return;
    }
    setEditingCategories(editingCategories.filter((_, i) => i !== index));
  };

  const handleSaveCategories = () => {
    // Check for duplicate names
    const uniqueCategories = new Set(editingCategories);
    if (uniqueCategories.size !== editingCategories.length) {
      alert('Kategorie nemohou mít stejný název!');
      return;
    }

    // Update menu items with new category names
    const updatedMenuItems = menuItems.map(item => {
      const oldCategory = item.category;
      const newCategory = editingCategories.find(cat => cat === oldCategory);
      return newCategory ? { ...item, category: newCategory } : item;
    });
    setMenuItems(updatedMenuItems);

    // Update categories
    setCustomCategories(editingCategories);
    setShowCategoryModal(false);
  };

  const getCategoryDisplayName = (category) => {
    switch(category) {
      case 'main':
        return 'Hlavní jídlo';
      case 'drink':
        return 'Nápoj';
      case 'dessert':
        return 'Dezert';
      default:
        return category; // For custom categories, use the name as is
    }
  };

  // Add this helper class for visually hidden labels
  const visuallyHidden = {
    position: 'absolute',
    width: '1px',
    height: '1px',
    padding: 0,
    margin: '-1px',
    overflow: 'hidden',
    clip: 'rect(0,0,0,0)',
    border: 0,
  };

  return (
    <div className="menu-management" role="main">
      <h2>Správa menu</h2>

      {/* Add/Edit Form */}
      <div className="menu-form card" ref={formRef} role="form" aria-label={editingId ? 'Upravit položku menu' : 'Přidat novou položku do menu'}>
        <h3>{editingId ? 'Upravit položku' : 'Přidat novou položku'}</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="itemName">Název:</label>
            <input
              type="text"
              id="itemName"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
              aria-label="Název položky menu"
            />
          </div>

          <div className="form-group">
            <label htmlFor="itemPrice">Cena:</label>
            <input
              type="number"
              id="itemPrice"
              name="price"
              value={formData.price}
              onChange={handleInputChange}
              required
              step="0.01"
              aria-label="Cena položky v Kč"
            />
          </div>

          <div className="form-group">
            <label htmlFor="itemCategory">Kategorie:</label>
            <select
              id="itemCategory"
              name="category"
              value={formData.category}
              onChange={handleInputChange}
              required
              aria-label="Vyberte kategorii položky"
            >
              <option value="main">Hlavní jídlo</option>
              <option value="drink">Nápoj</option>
              <option value="dessert">Dezert</option>
              {customCategories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          <div className="form-actions">
            <button 
              type="submit"
              aria-label={editingId ? 'Uložit změny' : 'Přidat položku'}
            >
              {editingId ? 'Uložit změny' : 'Přidat položku'}
            </button>
            {editingId && (
              <button 
                type="button" 
                onClick={() => {
                  setEditingId(null);
                  setFormData({ name: '', price: '', category: 'main' });
                }}
                aria-label="Zrušit úpravu"
              >
                Zrušit
              </button>
            )}
          </div>
        </form>
      </div>

      {/* Category Management */}
      <div className="category-management card">
        <h3>Správa kategorií</h3>
        {!showCategoryForm ? (
          <button 
            onClick={() => setShowCategoryForm(true)}
            aria-label="Přidat novou kategorii"
          >
            Přidat kategorii
          </button>
        ) : (
          <div className="category-form">
            <input
              type="text"
              value={newCategoryName}
              onChange={(e) => setNewCategoryName(e.target.value)}
              placeholder="Název kategorie"
              aria-label="Název nové kategorie"
            />
            <div className="category-form-actions">
              <button 
                onClick={handleAddCategory}
                aria-label="Uložit novou kategorii"
              >
                Uložit
              </button>
              <button 
                onClick={() => setShowCategoryForm(false)}
                aria-label="Zrušit přidávání kategorie"
              >
                Zrušit
              </button>
            </div>
          </div>
        )}

        <button 
          onClick={openCategoryModal}
          aria-label="Upravit existující kategorie"
        >
          Upravit kategorie
        </button>
      </div>

      {/* Menu Items List */}
      <div className="menu-items-section">
        <div className="category-filter" role="tablist" aria-label="Filtry kategorií">
          <button
            className={`category-btn ${selectedCategory === 'all' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('all')}
            role="tab"
            aria-selected={selectedCategory === 'all'}
            aria-label="Zobrazit všechny položky"
          >
            Vše
          </button>
          <button
            className={`category-btn ${selectedCategory === 'main' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('main')}
            role="tab"
            aria-selected={selectedCategory === 'main'}
            aria-label="Zobrazit hlavní jídla"
          >
            Hlavní jídla
          </button>
          <button
            className={`category-btn ${selectedCategory === 'drink' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('drink')}
            role="tab"
            aria-selected={selectedCategory === 'drink'}
            aria-label="Zobrazit nápoje"
          >
            Nápoje
          </button>
          <button
            className={`category-btn ${selectedCategory === 'dessert' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('dessert')}
            role="tab"
            aria-selected={selectedCategory === 'dessert'}
            aria-label="Zobrazit dezerty"
          >
            Dezerty
          </button>
          {customCategories.map(category => (
            <button
              key={category}
              className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category)}
              role="tab"
              aria-selected={selectedCategory === category}
              aria-label={`Zobrazit ${category}`}
            >
              {category}
            </button>
          ))}
        </div>

        <div className="menu-items-list" role="tabpanel">
          {selectedCategory === 'all' ? (
            groupedMenuItems.map(({ category, items }) => (
              <div key={category} className="menu-category">
                <h3 className="category-title">{getCategoryDisplayName(category)}</h3>
                <ul className="menu-items" role="list">
                  {items.map(item => (
                    <li key={item.id} className="menu-item" role="listitem">
                      <div className="item-details">
                        <span>{item.name}</span>
                        <span aria-label={`${item.price} Kč`}>{item.price} Kč</span>
                      </div>
                      <div className="item-actions">
                        <button 
                          onClick={() => editItem(item.id)}
                          aria-label={`Upravit ${item.name}`}
                        >
                          Upravit
                        </button>
                        <button 
                          onClick={() => requestDeleteItem(item.id)}
                          aria-label={`Smazat ${item.name}`}
                        >
                          Smazat
                        </button>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            ))
          ) : (
            <ul className="menu-items" role="list">
              {filteredMenuItems.map(item => (
                <li key={item.id} className="menu-item" role="listitem">
                  <div className="item-details">
                    <span>{item.name}</span>
                    <span aria-label={`${item.price} Kč`}>{item.price} Kč</span>
                  </div>
                  <div className="item-actions">
                    <button 
                      onClick={() => editItem(item.id)}
                      aria-label={`Upravit ${item.name}`}
                    >
                      Upravit
                    </button>
                    <button 
                      onClick={() => requestDeleteItem(item.id)}
                      aria-label={`Smazat ${item.name}`}
                    >
                      Smazat
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      {deleteConfirmId && (
        <div className="modal" role="dialog" aria-label="Potvrzení smazání">
          <div className="modal-content">
            <h3>Smazat položku?</h3>
            <p>Tuto akci nelze vrátit zpět.</p>
            <div className="modal-actions">
              <button 
                onClick={confirmDelete}
                aria-label="Potvrdit smazání"
              >
                Smazat
              </button>
              <button 
                onClick={cancelDelete}
                aria-label="Zrušit smazání"
              >
                Zrušit
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Category Edit Modal */}
      {showCategoryModal && (
        <div className="modal" role="dialog" aria-label="Úprava kategorií">
          <div className="modal-content">
            <h3>Upravit kategorie</h3>
            <div className="category-list" role="list">
              {editingCategories.map((category, index) => (
                <div key={index} className="category-item" role="listitem">
                  <input
                    type="text"
                    value={category}
                    onChange={(e) => handleCategoryChange(index, e.target.value)}
                    aria-label={`Upravit název kategorie ${category}`}
                  />
                  <button 
                    onClick={() => handleDeleteCategory(index)}
                    aria-label={`Smazat kategorii ${category}`}
                  >
                    Smazat
                  </button>
                </div>
              ))}
            </div>
            <div className="modal-actions">
              <button 
                onClick={handleSaveCategories}
                aria-label="Uložit změny kategorií"
              >
                Uložit
              </button>
              <button 
                onClick={() => setShowCategoryModal(false)}
                aria-label="Zrušit úpravu kategorií"
              >
                Zrušit
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MenuManagement;
